import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        console.log("Fetching posts..."); // Log before fetching
        const response = await fetch("http://localhost:3000/posts/"); // Adjust API URL if needed
        const data = await response.json();
        console.log("Fetched posts data:", data);  // Log the data received from the backend
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const categories = [
    {
      name: "Club",
      subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Happy Club"],
      image: "/images/clubs-image.jpg",
    },
    {
      name: "Academics",
      subcategories: ["First year", "Second year", "Third year", "Fourth year"],
      image: "/images/academic excellence.jpg",
    },
    {
      name: "Notices",
      subcategories: [],
      image: "/images/notice.jpg",
    },
    {
      name: "Skill Hub",
      subcategories: ["Hackathons", "Coding-Contests", "Training programmes"],
      image: "/images/skill-hub.jpg",
    },
    {
      name: "Events",
      subcategories: [],
      image: "/images/events-icon.jpg",
    },
    {
      name: "Placements",
      subcategories: [],
      image: "/images/placements.jpg",
    },
  ];

  console.log("Posts array in state:", posts); // Log the posts array in the component state

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>

      {/* CATEGORY SECTION */}
      <div className="category-container">
        {categories.map((category) => (
          <div
            key={category.name}
            className="category-wrapper"
            onMouseEnter={() => setActiveCategory(category.name)}
            onMouseLeave={() => setActiveCategory(null)}
          >
            <div className="category-icon">
              <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
            </div>
            <div className="category-name">{category.name}</div>
            {category.subcategories.length > 0 && activeCategory === category.name && (
              <div className="sub-category-container">
                <ul>
                  {category.subcategories.map((subcategory, subIndex) => (
                    <li key={subIndex} className="subcategory-item">
                      {subcategory === "RMF" ? (
                        <Link to="/rmfpage" className="subcategory-link">
                          RMF
                        </Link>
                      ) : (
                        subcategory
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* FEED SECTION */}
      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {posts
            .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort posts by createdAt in descending order
            .map((post, index) => (
              <div className="feed-item" key={index}>
                <div className="feed-image">
                  {/* Check if there is at least one media item to display */}
                  {post.media.length > 0 && post.media[0].url && (
                    <img src={post.media[0].url} alt={post.title} />
                  )}
                </div>
                <div className="feed-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  <p>
                    <strong>Category:</strong> {post.category}
                  </p>
                  <p>
                    <strong>Subcategory:</strong> {post.subCategory}
                  </p>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
