import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";  
import axiosInstance from "./api/axiosfetch"; // Use axios instance
import "./Postspage.css";

const Postspage = ({ userEmail }) => {  
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let email = userEmail || localStorage.getItem("userEmail"); // Get from prop or localStorage
    console.log("üîç User email being used:", email);

    if (!email) {
      console.error("‚ö†Ô∏è No user email available, skipping fetch.");
      return;
    }

    const fetchPosts = async () => {
      try {
        console.log("üì¢ Fetching posts for:", email);
        const response = await axiosInstance.get(`/postview/${email}`);
        console.log("‚úÖ Posts fetched:", response.data);
        setPosts(response.data);
      } catch (error) {
        console.error("‚ùå Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [userEmail]);

  return (
    <div className="posts-container">
      <h1>User Posts</h1>

      {/* New Post Button */}
      <button className="new-post-button" onClick={() => navigate("/newpost")}>
         New Post
      </button>

      {posts.length === 0 ? (
        <p>No posts available.</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} className="post-card">
            <h3>{post.title}</h3>
            <p>{post.description}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Postspage;
