import React, { useState } from "react";
import axios from "axios";
import "./Newpost.css";

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]); // Array to store selected files
  const [files, setFiles] = useState([]); // Array to store actual file objects
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [uploaderEmail] = useState("uploader@example.com"); // Replace with actual email

  // Handle file selection
  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFileNames((prevFileNames) => [
      ...prevFileNames,
      ...selectedFiles.map((file) => file.name),
    ]);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
  };

  // Remove file from the list
  const handleRemoveFile = (fileName) => {
    setFileNames((prevFileNames) => prevFileNames.filter((name) => name !== fileName));
    setFiles((prevFiles) => prevFiles.filter((file) => file.name !== fileName));
  };

  // Handle form submission
  const handlePost = async () => {
    const formData = new FormData();
    files.forEach((file) => formData.append("media", file)); // Append files
    formData.append("title", title);
    formData.append("description", description);
    formData.append("uploaderEmail", uploaderEmail);
    formData.append("category", category);
    formData.append("subCategory", subCategory);

    try {
      const response = await axios.post("/create-post", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Post submitted successfully!");
      console.log(response.data);
    } catch (error) {
      console.error(error);
      alert("Failed to submit post.");
    }
  };

  // Handle saving the post as a draft
  const handleSaveDraft = () => {
    alert("Post saved as draft!");
    console.log({
      title,
      description,
      category,
      subCategory,
      fileNames,
    });
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        {/* File upload section */}
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0
              ? fileNames.join(", ")
              : "Click to browse or drop files here"}
          </label>
          <input
            type="file"
            id="fileInput"
            multiple
            onChange={handleFileChange}
            style={{ display: "none" }}
          />
        </div>

        {/* Title Input */}
        <input
          type="text"
          placeholder="Enter title for the file"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        {/* Description Input */}
        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        {/* Category Selection */}
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        >
          <option value="" disabled>Select a category</option>
          <option value="category1">Category 1</option>
          <option value="category2">Category 2</option>
        </select>

        {/* Subcategory Selection */}
        <select
          value={subCategory}
          onChange={(e) => setSubCategory(e.target.value)}
          required
        >
          <option value="" disabled>Select a subcategory</option>
          <option value="subcategory1">Subcategory 1</option>
          <option value="subcategory2">Subcategory 2</option>
        </select>

        {/* Buttons */}
        <div className="button-container">
          <button type="button" className="save-draft" onClick={handleSaveDraft}>
            Save as Draft
          </button>
          <button type="button" className="post" onClick={handlePost}>
            Post
          </button>
        </div>

        {/* Display the list of uploaded files with Remove option */}
        {fileNames.length > 0 && (
          <div className="uploaded-files">
            <h4>Uploaded Files:</h4>
            <ul>
              {fileNames.map((fileName, index) => (
                <li key={index}>
                  {fileName}
                  <span
                    onClick={() => handleRemoveFile(fileName)}
                    className="remove-file"
                  >
                    ×
                  </span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </form>
    </div>
  );
};

export default NewPost;
