// DashboardContent.js

import React, { useState } from 'react';
import './DashboardContent.css';

const DashboardContent = () => {
  const [selectedCategory, setSelectedCategory] = useState('Category1');
  const [textExpanded, setTextExpanded] = useState(false);

  const images = selectedCategory === 'Category1'
    ? ['/image1.jpg', '/image2.jpg']
    : ['/image3.jpg', '/image4.jpg'];

  const description =
    'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sed ipsum quis libero tincidunt euismod. Morbi accumsan, orci nec tempus dictum, justo felis viverra lorem, nec malesuada nisi ipsum vel...';

  return (
    <div className="dashboard-content">
      <div className="buttons-container">
        <button onClick={() => setSelectedCategory('Category1')}>Category 1</button>
        <button onClick={() => setSelectedCategory('Category2')}>Category 2</button>
      </div>

      <div className="content">
        {/* Left side - Images carousel */}
        <div className="images-container">
          {images.map((image, index) => (
            <img src={image} alt={`img-${index}`} className="image" key={index} />
          ))}
        </div>

        {/* Right side - Text with Read More */}
        <div className="text-container">
          <p className="description">
            {textExpanded ? description : description.substring(0, 150) + '...'}
          </p>
          <button
            className="read-more-button"
            onClick={() => setTextExpanded(!textExpanded)}
          >
            {textExpanded ? 'Read Less' : 'Read More'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
