import React, { useState } from "react";
import axios from "axios"; // For making HTTP requests
import "./Login.css"; // Optional: Add CSS styling for the component

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Replace the URL with your backend login endpoint
      const response = await axios.post("http://localhost:5000/api/login", {
        email,
        password,
      });

      // Assuming response contains user role (e.g., "student" or "coordinator")
      const { role, token } = response.data;

      // Save token in local storage (or cookies) for session management
      localStorage.setItem("authToken", token);

      // Redirect user based on their role
      if (role === "student") {
        window.location.href = "/student-dashboard";
      } else if (role === "coordinator") {
        window.location.href = "/coordinator-dashboard";
      }
    } catch (err) {
      setError(err.response?.data?.message || "Login failed. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
