import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const NewPost = ({ userEmail }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [media, setMedia] = useState([]);
  const [filePreviews, setFilePreviews] = useState([]);
  const location = useLocation();
  const draft = location.state?.draft || {};

  useEffect(() => {
    if (draft) {
      setTitle(draft.title || "");
      setDescription(draft.description || "");
      setCategory(draft.category || "");
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setFilePreviews(draft.media || []);
    }
  }, [draft]);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setMedia(files);

    const previews = files.map((file) => ({
      url: URL.createObjectURL(file),
      type: file.type,
    }));
    setFilePreviews(previews);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("email", userEmail);
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("link", link);
    media.forEach((file) => formData.append("media", file));

    try {
      const response = draft._id
        ? await axios.put(`http://localhost:5000/api/posts/${draft._id}`, formData)
        : await axios.post("http://localhost:5000/api/posts", formData);

      alert(`Post ${draft._id ? "updated" : "created"} successfully!`);
    } catch (error) {
      console.error("Error saving post:", error);
      alert("Failed to save the post.");
    }
  };

  return (
    <div className="new-post-container">
      <h1>{draft._id ? "Edit Draft" : "New Post"}</h1>
      <form onSubmit={handleSubmit}>
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <label>Description</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label>Category</label>
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />

        <label>Sub-Category</label>
        <input
          type="text"
          value={subCategory}
          onChange={(e) => setSubCategory(e.target.value)}
          required
        />

        <label>Link</label>
        <input
          type="url"
          value={link}
          onChange={(e) => setLink(e.target.value)}
        />

        <label>Media</label>
        <input type="file" multiple onChange={handleFileChange} />
        <div className="media-preview">
          {filePreviews.map((file, index) => (
            file.type.startsWith("image/") ? (
              <img
                key={index}
                src={file.url}
                alt="Preview"
                className="preview-item"
              />
            ) : (
              <a key={index} href={file.url} target="_blank" rel="noopener noreferrer">
                View File
              </a>
            )
          ))}
        </div>

        <button type="submit">{draft._id ? "Update Draft" : "Create Post"}</button>
      </form>
    </div>
  );
};

export default NewPost;
