import React, { useState, useEffect } from 'react';
import './DashboardContent.css';

const DashboardContent = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSubCategory, setSelectedSubCategory] = useState('');
  const [textExpanded, setTextExpanded] = useState(false);
  const [images] = useState({
    Category1: [
      ['/image1.jpg', '/image2.jpg', '/image3.jpg'],
      ['/image7.jpg', '/image8.jpg', '/image9.jpg']
    ],
    Category2: [
      ['/image4.jpg', '/image5.jpg', '/image6.jpg'],
      ['/image10.jpg', '/image11.jpg', '/image12.jpg']
    ]
  });
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const categories = {
    Category1: ['Subcategory1', 'Subcategory2'],
    Category2: ['Subcategory3', 'Subcategory4']
  };

  useEffect(() => {
    // Automatically slide images every 3 seconds
    const interval = setInterval(() => {
      setCurrentImageIndex(prevIndex => (prevIndex + 1) % images[selectedCategory][0].length);
    }, 3000);

    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, [selectedCategory, selectedSubCategory, images]);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSelectedSubCategory(''); // Reset subcategory when category is changed
  };

  const handleSubCategoryClick = (subCategory) => {
    setSelectedSubCategory(subCategory);
  };

  // Check if the selectedSubCategory exists and is valid
  const getImagesForSubCategory = () => {
    if (!selectedCategory || !selectedSubCategory) return images[Object.keys(images)[0]][0]; // Default to first category and first set of images
    const categoryIndex = categories[selectedCategory].indexOf(selectedSubCategory);
    if (categoryIndex === -1) return images[Object.keys(images)[0]][0]; // Return default images if subcategory is invalid
    return images[selectedCategory][categoryIndex];
  };

  return (
    <div className="dashboard-content">
      <div className="buttons-container">
        {Object.keys(categories).map(category => (
          <div key={category}>
            <button onClick={() => handleCategoryClick(category)}>{category}</button>
            {selectedCategory === category && (
              <div className="dropdown">
                {categories[category].map(subCategory => (
                  <button
                    key={subCategory}
                    onClick={() => handleSubCategoryClick(subCategory)}
                    className="dropdown-item"
                  >
                    {subCategory}
                  </button>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="content">
        {/* Left side - Images carousel */}
        <div className="images-container">
          <img
            src={getImagesForSubCategory()[currentImageIndex]}
            alt={`img-${currentImageIndex}`}
            className="image"
          />
        </div>

        {/* Right side - Text with Read More */}
        <div className="text-container">
          <p className="description">
            {textExpanded
              ? 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sed ipsum quis libero tincidunt euismod. Morbi accumsan, orci nec tempus dictum, justo felis viverra lorem, nec malesuada nisi ipsum vel...'
              : 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sed ipsum quis libero tincidunt euismod...'}
          </p>
          <button
            className="read-more-button"
            onClick={() => setTextExpanded(!textExpanded)}
          >
            {textExpanded ? 'Read Less' : 'Read More'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
