import React, { useContext, useState } from "react";
import { AuthContext } from "./contextauth"; // Import AuthContext
import "./Newpost.css";

const NewPost = () => {
  const { userEmail } = useContext(AuthContext); // Access email from context
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [files, setFiles] = useState([]);

  console.log("User Email:", userEmail); // Log email to check what is passed

  // Handle file change
  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
    setFileNames((prevNames) => [...prevNames, ...selectedFiles.map((file) => file.name)]);
  };

  // Handle file removal
  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFileNames((prevNames) => prevNames.filter((_, i) => i !== index));
  };

  // Reset form
  const resetForm = () => {
    setFileNames([]);
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setFiles([]);
  };

  // Handle submit
  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail); // Add userEmail to formData

    files.forEach((file) => formData.append("media", file));

    try {
      const response = await fetch("http://localhost:3000/posts/create-post", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const message = isDraft ? "Draft saved successfully!" : "Post submitted successfully!";
        alert(message);
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        {/* Form content */}
        <input
          type="text"
          placeholder="Enter title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
        {/* Submit buttons */}
        <button type="button" onClick={() => handleSubmit(false)}>
          Submit Post
        </button>
        <button type="button" onClick={() => handleSubmit(true)}>
          Save as Draft
        </button>
      </form>
    </div>
  );
};

export default NewPost;
