  skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
};

const categories = [
  {
    name: "Events",
    value: "events",
    image: "/images/events-icon.jpg",
  },
  {
    name: "Clubs",
    value: "clubs",
    image: "/images/clubs-image.jpg",
  },
  {
    name: "Academics",
    value: "academics",
    image: "/images/academic-excellence.jpg",
  },
  {
    name: "Notices",
    value: "notices",
    image: "/images/notice.jpg",
  },
  {
    name: "Skill Hub",
    value: "skillhub",
    image: "/images/skill-hub.jpg",
  },
  {
    name: "Placements",
    value: "placements",
    image: "/images/placements.jpg",
  },
];

const RMFpage = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState("");
  const [activeSubCategory, setActiveSubCategory] = useState("");
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const subCategoryFromURL = queryParams.get("subcategory");

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(`http://localhost:3000/posts?subcategory=${subCategoryFromURL}`);
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, [subCategoryFromURL]);

  return (
    <div className="outer-box">
      <div className="left-container">
        <h1>Dashboard</h1>
        <div className="category-container">
          {categories.map((category) => (
            <div
              key={category.value}
              className="category-wrapper"
              onClick={() => {
                setActiveCategory(category.value);
                setActiveSubCategory("");
              }}
            >
              <div className="category-icon">
                <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
              </div>
              <div className="category-name">{category.name}</div>
            </div>
          ))}
        </div>
      </div>

      <div className="feed-section-postpage">
        <h2>{subCategoryFromURL} - Posts</h2>
        <div className="feed-grid-postpage">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts.map((post) => (
              <div className="feed-item-postpage" key={post._id}>
                <h3>{post.title}</h3>
                <p>{post.description}</p>

                {post.fileType === "pdf" && (
                  <a href={post.fileUrl} target="_blank" rel="noopener noreferrer">
                    View PDF
                  </a>
                )}

                {post.fileType === "video" && (
                  <video controls width="100%">
                    <source src={post.fileUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                )}

                {post.fileType === "image" && (
                  <img src={post.fileUrl} alt="Post" style={{ width: "100%", height: "auto" }} />
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default RMFpage;
