import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosInstance";
import { AuthContext } from "./contextauth";  // Make sure the context is defined
import "./Login.css";

const Login = () => {
  const { login } = useContext(AuthContext);  // Access the login function from AuthContext
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Login submitted with email:", email, "and password:", password);

    try {
      const response = await axiosInstance.post("/login", { email, password });

      // Log the full response from the backend
      console.log("Full response from backend:", response);

      // Destructure the response data
      const { role, redirectTo, email: userEmail } = response.data;

      // Check if email is received correctly from the backend
      console.log("Received email from backend:", userEmail);
      console.log("Role from backend:", role);

      // Store login details in Context API
      login(userEmail, role);

      // Navigate to the correct dashboard based on the role
      if (role === "coordinator") {
        console.log("Redirecting to coordinator dashboard...");
        navigate("/coordinatordashboard");  // Example path, update with your actual coordinator dashboard route
      } else if (role === "student") {
        console.log("Redirecting to student dashboard...");
        navigate("/studentdashboard");  // Example path, update with your actual student dashboard route
      } else {
        console.log("Redirecting to:", redirectTo || "/");
        navigate(redirectTo || "/");  // Default redirect if no specific role is found
      }

    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.message || "Invalid login credentials");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group password-input">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
