import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [sortOrder, setSortOrder] = useState("desc");
  const [activeCategory, setActiveCategory] = useState(null);
  const [showFilterOptions, setShowFilterOptions] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  // Filter and Search
  const filteredPosts = posts.filter((post) =>
    Object.values(post).some((value) =>
      typeof value === "string" &&
      value.toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  // Sort Posts
  const sortedPosts = filteredPosts.sort((a, b) =>
    sortOrder === "desc"
      ? new Date(b.createdAt) - new Date(a.createdAt)
      : new Date(a.createdAt) - new Date(b.createdAt)
  );

  const categories = [
    { name: "Club", subcategories: ["RMF", "Splash Out"], image: "/images/clubs-image.jpg" },
    { name: "Academics", subcategories: ["First Year", "Second Year"], image: "/images/academic-excellence.jpg" },
  ];

  return (
    <div className="outer-box">
      <div className="left-container">
        <div className="dashboard-section">
          <h1>Dashboard</h1>
          <div className="category-container">
            {categories.map((category) => (
              <div
                key={category.name}
                className="category-wrapper"
                onMouseEnter={() => setActiveCategory(category.name)}
                onMouseLeave={() => setActiveCategory(null)}
              >
                <div className="category-icon">
                  <img src={category.image} alt={`${category.name} Icon`} />
                </div>
                <div>{category.name}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="feed-section">
          <div className="feed-header">
            <input
              type="text"
              className="search-bar"
              placeholder="Search posts..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button
              className="filter-button"
              onClick={() => setShowFilterOptions(!showFilterOptions)}
            >
              Filter
            </button>
            {showFilterOptions && (
              <div className="filter-dropdown">
                <button onClick={() => setSortOrder("desc")}>Newest First</button>
                <button onClick={() => setSortOrder("asc")}>Oldest First</button>
              </div>
            )}
          </div>
          <div className="feed-grid">
            {sortedPosts.map((post, index) => (
              <div className="feed-item" key={index}>
                <h3>{post.title}</h3>
                <p>{post.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="analytics-container">
        <h2>Analytics</h2>
        <img src="/images/powerbi-report.jpg" alt="Power BI Analytics" />
      </div>
    </div>
  );
};

export default DashboardContent;
