import React, { useState } from "react";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState(""); // 🔹 New state for the link field
  const [files, setFiles] = useState([]);
  const [showCancelDialog, setShowCancelDialog] = useState(false); // 🔹 For confirmation popup

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    const allowedFormats = ["application/pdf", "video/mp4", "video/webm", "video/ogg", "image/gif"];
  
    const filteredFiles = selectedFiles.filter((file) => {
      if (!allowedFormats.includes(file.type)) {
        alert(`File "${file.name}" is not a supported format. Only PDF, MP4, WebM, OGG, and GIF are allowed.`);
        return false;
      }
      return true;
    });
  
    setFiles((prevFiles) => [...prevFiles, ...filteredFiles]);
  };
  

  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFileNames((prevNames) => prevNames.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    setFileNames([]);
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setLink("");
    setFiles([]);
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");  
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link); // 🔹 Add link to form data

    files.forEach((file) => formData.append("media", file));

    try {
      const response = await fetch("http://localhost:3000/posts/create-post", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        {/* File Upload Section (Images, Videos, Documents) */}
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0 ? "Add more files" : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" onChange={handleFileChange} multiple style={{ display: "none" }} />
        </div>

        <div className="uploaded-files">
          {fileNames.map((fileName, index) => (
            <div key={index} className="file-item">
              <span>{fileName}</span>
              <button type="button" className="remove-button" onClick={() => handleFileRemove(index)}>
                ❌
              </button>
            </div>
          ))}
        </div>

        {/* Text Fields */}
        <input type="text" placeholder="Enter title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea placeholder="Write a description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>

        {/* Link Field */}
        <input type="text" placeholder="Enter link (if any)" value={link} onChange={(e) => setLink(e.target.value)} />

        {/* Category Selection */}
        <select value={category} onChange={(e) => { setCategory(e.target.value); setSubCategory(""); }} required>
          <option value="" disabled>Select a category</option>
          <option value="events">Events</option>
          <option value="clubs">Clubs</option>
          <option value="academics">Academics</option>
          <option value="notices">Notices</option>
          <option value="skillhub">Skillhub</option>
          <option value="placements">Placements</option>
        </select>

        {/* Subcategory Dropdown (if applicable) */}
        {category && subCategoriesMap[category]?.length > 0 && (
          <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)}>
            <option value="" disabled>Select a subcategory</option>
            {subCategoriesMap[category].map((sub, index) => (
              <option key={index} value={sub.toLowerCase()}>{sub}</option>
            ))}
          </select>
        )}

        {/* Buttons */}
        <div className="button-container">
          <button type="button" onClick={() => handleSubmit(false)}>Submit Post</button>
          <button type="button" onClick={() => handleSubmit(true)}>Save as Draft</button>
          <button type="button" className="cancel-button" onClick={() => setShowCancelDialog(true)}>Cancel</button>
        </div>
      </form>

      {/* Cancel Confirmation Dialog */}
      {showCancelDialog && (
        <div className="popup-overlay">
          <div className="popup">
            <p>Are you sure you want to cancel this post?</p>
            <button className="confirm-btn" onClick={() => { setShowCancelDialog(false); resetForm(); }}>Yes, Cancel</button>
            <button className="cancel-btn" onClick={() => setShowCancelDialog(false)}>No, Keep Editing</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewPost;
