import React from "react";
import "./Home.css";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const HomePage = () => {
  return (
    <div className="container">
      {/* Left Section - Text */}
      <div className="left-section">
        <h1>College Chronicle</h1>
        <h2>Welcome to College Chronicle</h2>
        <p>
          Experience the vibrant life of our campus, where education meets
          opportunities. Learn, grow, and achieve your dreams.
        </p>
        <button className="login-button">Login</button>
      </div>

      {/* Right Section - Image Carousel */}
      <div className="right-section">
        <Carousel
          showThumbs={false}
          infiniteLoop={true}
          autoPlay={true}
          interval={3000}
          showStatus={false}
        >
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 1" />
            <p className="legend">Campus Life</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 2" />
            <p className="legend">Events</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 3" />
            <p className="legend">Learning Spaces</p>
          </div>
        </Carousel>
      </div>
    </div>
  );
};

export default HomePage;
