import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import "./DraftsPage.css";

const DraftsPage = ({ userEmail }) => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let email = userEmail || localStorage.getItem("userEmail");
    console.log("🔍 Fetching drafts for:", email);

    if (!email) {
      console.error("⚠️ No user email available, skipping fetch.");
      return;
    }

    const fetchDrafts = async () => {
      try {
        const response = await axiosInstance.get(`/draftsview/${encodeURIComponent(email)}`);
        console.log("✅ Drafts fetched:", response.data);
        setDrafts(response.data);
      } catch (error) {
        console.error("❌ Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, [userEmail]);

  const handleViewDraft = (draft) => {
    navigate("/Newpost", {
      state: { draft: draft },
    });
  };

  return (
    <div className="drafts-container">
      <h1>Saved Drafts</h1>

      {drafts.length === 0 ? (
        <p>No drafts available.</p>
      ) : (
        <div className="feed-grid">
          {drafts.map((draft) => (
            <div key={draft._id} className="feed-item">
              <div className="feed-content">
                <h3>{draft.title}</h3>
                <p>{draft.description}</p>
                <button className="view-draft-button" onClick={() => handleViewDraft(draft)}>
                  View
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default DraftsPage;
