import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosInstance"; // Ensure this points to your backend API
import "./Login.css";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);  // Loading state for form submission
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Set loading to true when the login attempt starts
    setError(""); // Clear any previous error messages

    try {
      const response = await axiosInstance.post("/login", { email, password });
      const { role, redirectTo, email: userEmail } = response.data;

      // Save user data to localStorage for persistence
      localStorage.setItem("userRole", role);
      localStorage.setItem("userEmail", userEmail);

      // Trigger onLogin callback and navigate
      onLogin(role, userEmail);
      navigate(redirectTo); // Redirect user based on their role
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.message || "Invalid login credentials");
    } finally {
      setLoading(false); // Set loading to false after request is completed
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>} {/* Display error message */}
        
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
          />
        </div>
        
        <div className="form-group password-input">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
          />
        </div>
        
        <button type="submit" className="login-button" disabled={loading}>
          {loading ? "Logging in..." : "Login"} {/* Show loading state in button */}
        </button>
      </form>
    </div>
  );
};

export default Login;
