import React, { useState } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const DashboardContent = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState({});
  const [showMore, setShowMore] = useState({});

  const feedData = [
    {
      images: [
        "https://via.placeholder.com/150",
        "https://via.placeholder.com/150/0000FF",
      ],
      text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec a nisi vitae augue vehicula viverra.",
    },
    {
      images: ["https://via.placeholder.com/150"],
      text: "Short content text.",
    },
  ];

  const handleCategoryClick = (category) => {
    setSelectedCategory((prev) => (prev === category ? null : category));
  };

  const handleSubCategoryChange = (category, subCategory) => {
    setSelectedSubCategory((prev) => ({ ...prev, [category]: subCategory }));
  };

  const toggleShowMore = (index) => {
    setShowMore((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {["Category1", "Category2", "Button3", "Button4", "Button5", "Button6"].map(
          (category) => (
            <div key={category} style={{ textAlign: "center" }}>
              <button onClick={() => handleCategoryClick(category)}>{category}</button>
              {selectedCategory === category && (
                <div>
                  <label>
                    <select
                      onChange={(e) =>
                        handleSubCategoryChange(category, e.target.value)
                      }
                    >
                      <option value="">--Select Subcategory--</option>
                      <option value="Subcategory1">Subcategory 1</option>
                      <option value="Subcategory2">Subcategory 2</option>
                    </select>
                  </label>
                  {selectedSubCategory[category] && (
                    <p>Selected: {selectedSubCategory[category]}</p>
                  )}
                </div>
              )}
            </div>
          )
        )}
      </div>

      <div style={{ marginTop: "20px" }}>
        <h2>Feed</h2>
        {feedData.map((item, index) => (
          <div
            key={index}
            style={{
              display: "flex",
              alignItems: "flex-start",
              marginBottom: "20px",
              border: "1px solid #ccc",
              padding: "10px",
            }}
          >
            <div style={{ width: "30%" }}>
              <Carousel
                showThumbs={false}
                infiniteLoop={true}
                autoPlay={true}
                interval={3000}
              >
                {item.images.map((image, imgIndex) => (
                  <div key={imgIndex}>
                    <img
                      src={image}
                      alt={`Slide ${index}-${imgIndex}`}
                      style={{ width: "100%", height: "auto" }}
                    />
                  </div>
                ))}
              </Carousel>
            </div>
            <div style={{ width: "70%", paddingLeft: "10px" }}>
              <p>
                {showMore[index] || item.text.length <= 100
                  ? item.text
                  : `${item.text.substring(0, 100)}...`}
              </p>
              {item.text.length > 100 && (
                <button onClick={() => toggleShowMore(index)}>
                  {showMore[index] ? "Read Less" : "Read More"}
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardContent;
