import React, { useState, useEffect } from "react";
import axiosInstance from "./api/axiosInstance"; // Ensure the Axios instance is correctly configured
import "./StudentProfile.css";

const StudentProfile = () => {
  const [profileImage, setProfileImage] = useState("https://via.placeholder.com/100");
  const [showOptions, setShowOptions] = useState(false);
  const [studentDetails, setStudentDetails] = useState({
    name: "",
    email: "",
    studentid: "",
    branch: "",
    phoneno: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    // Fetch student details on component mount
    const fetchStudentDetails = async () => {
      try {
        const email = localStorage.getItem("email"); // Assuming the email is stored in localStorage after login
        if (!email) {
          setError("No email found. Please log in again.");
          return;
        }

        const response = await axiosInstance.get(`/studentinfo/${email}`);
        setStudentDetails(response.data); // Update state with student details
      } catch (err) {
        console.error("Error fetching student details:", err);
        setError("Failed to fetch student details. Please try again later.");
      }
    };

    fetchStudentDetails();
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setProfileImage(reader.result);
      reader.readAsDataURL(file);
    }
    setShowOptions(false);
  };

  const handleCaptureImage = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement("video");
      video.srcObject = stream;
      video.play();

      await new Promise((resolve) => (video.onloadedmetadata = resolve));
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext("2d");
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      stream.getTracks().forEach((track) => track.stop());
      setProfileImage(canvas.toDataURL());
    } catch (error) {
      console.error("Error accessing the webcam:", error);
    }
    setShowOptions(false);
  };

  const handleDeleteImage = () => {
    setProfileImage("https://via.placeholder.com/100");
    setShowOptions(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-image-container">
          <img src={profileImage} alt="Profile" className="profile-image" />
          <button
            onClick={() => setShowOptions(!showOptions)}
            className="camera-icon-button"
          >
            <img
              src="/images/camera.jpg"
              alt="Camera Icon"
              className="camera-icon"
            />
          </button>
        </div>
        <h2 className="profile-username">{studentDetails.name || "N/A"}</h2>
        <p className="profile-role">Student</p>
        <div className="profile-details">
          <p>
            <strong>Name:</strong> {studentDetails.name || "N/A"}
          </p>
          <p>
            <strong>Email:</strong> {studentDetails.email || "N/A"}
          </p>
          <p>
            <strong>Student ID:</strong> {studentDetails.studentid || "N/A"}
          </p>
          <p>
            <strong>Department:</strong> {studentDetails.branch || "N/A"}
          </p>
          <p>
            <strong>Phone Number:</strong> {studentDetails.phoneno || "N/A"}
          </p>
        </div>
        {error && <p className="error">{error}</p>}
        {showOptions && (
          <div className="options-menu">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="upload-button"
            />
            <div className="capture-delete-row">
              <button onClick={handleCaptureImage} className="capture-button">
                Capture Image
              </button>
              <button onClick={handleDeleteImage} className="delete-button">
                <img
                  src="/images/delete.jpg"
                  alt="Delete"
                  className="dustbin-icon"
                />
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentProfile;
