import React, { useEffect, useState } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Link } from "react-router-dom";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [activeCategory, setActiveCategory] = useState(null);
  const [feedData, setFeedData] = useState([]);
  const [showMore, setShowMore] = useState({});

  useEffect(() => {
    document.body.classList.add("dashboard-page");
    return () => {
      document.body.classList.remove("dashboard-page");
    };
  }, []);

  useEffect(() => {
    fetch("http://localhost:5000/posts") // Adjust the API URL as needed
      .then((response) => response.json())
      .then((data) => {
        const sortedData = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        setFeedData(sortedData);
      })
      .catch((error) => console.error("Error fetching posts:", error));
  }, []);

  const toggleShowMore = (index) => {
    setShowMore((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {feedData.map((item, index) => (
            <div className="feed-item" key={index}>
              <div className="carousel-container">
                <Carousel showThumbs={false} infiniteLoop autoPlay interval={3000}>
                  {item.media.map((mediaItem, imgIndex) => (
                    <div key={imgIndex}>
                      <img src={mediaItem.url} alt={`Slide ${index}-${imgIndex}`} />
                    </div>
                  ))}
                </Carousel>
              </div>
              <div className="feed-content">
                <h3>{item.title}</h3>
                <p>
                  {showMore[index] || item.description.length <= 100
                    ? item.description
                    : `${item.description.substring(0, 100)}...`}
                </p>
                {item.description.length > 100 && (
                  <button
                    className="read-more-btn"
                    onClick={() => toggleShowMore(index)}
                  >
                    {showMore[index] ? "Read Less" : "Read More"}
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
