import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Newpost.css";


toast.configure();

const NewPost = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch draft data when editing an existing draft
  useEffect(() => {
    if (id) {
      axios
        .get(`/api/drafts/${id}`)
        .then((response) => {
          setTitle(response.data.title);
          setContent(response.data.content);
          if (response.data.imageUrl) {
            setImage(response.data.imageUrl); // Load existing image if available
          }
        })
        .catch((err) => console.error("Error fetching draft:", err));
    }
  }, [id]);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const uploadImageToCloudinary = async () => {
    if (!image || typeof image === "string") return image; // Keep existing image if no new one

    const formData = new FormData();
    formData.append("file", image);
    formData.append("upload_preset", "your_upload_preset");

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/your_cloud_name/image/upload",
        formData
      );
      return response.data.secure_url;
    } catch (error) {
      console.error("Image upload failed", error);
      toast.error("Image upload failed");
      return null;
    }
  };

  const handlePost = async () => {
    setLoading(true);
    const imageUrl = await uploadImageToCloudinary(); // Keep existing image if unchanged

    const postData = { title, content, status: "post", imageUrl };

    axios
      .put(`/api/drafts/${id}`, postData)
      .then(() => {
        toast.success("Post published successfully");
        navigate("/posts");
      })
      .catch(() => toast.error("Failed to publish post"))
      .finally(() => setLoading(false));
  };

  const handleDraft = async () => {
    setLoading(true);
    const imageUrl = await uploadImageToCloudinary();

    const draftData = { title, content, status: "draft", imageUrl };

    axios
      .put(`/api/drafts/${id}`, draftData)
      .then(() => toast.success("Draft saved successfully"))
      .catch(() => toast.error("Failed to save draft"))
      .finally(() => setLoading(false));
  };

  const handleCancel = () => {
    if (window.confirm("Are you sure you want to discard this draft?")) {
      axios
        .delete(`/api/drafts/${id}`)
        .then(() => {
          toast.success("Draft deleted");
          navigate("/drafts");
        })
        .catch(() => toast.error("Failed to delete draft"));
    }
  };

  return (
    <div>
      <h2>{id ? "Edit Draft" : "New Post"}</h2>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Content"
      />
      <input type="file" onChange={handleImageUpload} />
      {image && typeof image === "string" && (
        <div>
          <p>Current Image:</p>
          <img src={image} alt="Uploaded preview" width="200" />
        </div>
      )}
      <button onClick={handlePost} disabled={loading}>
        {loading ? "Posting..." : "Post"}
      </button>
      <button onClick={handleDraft} disabled={loading}>
        {loading ? "Saving..." : "Save Draft"}
      </button>
      <button onClick={handleCancel} disabled={loading}>Cancel</button>
    </div>
  );
};

export default NewPost;
