import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from './api/axiosInstance'; // Ensure this is correctly configured
import "./Login.css";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Login button clicked"); // Debugging: Ensure button is triggering

    try {
      const response = await axiosInstance.post("/login", { email, password });
      console.log("Login Response:", response.data); // Debugging: Log server response

      const { role, redirectTo, email: userEmail } = response.data;
      onLogin(role, userEmail); // Pass email and role to parent component
      navigate(redirectTo);
    } catch (err) {
      console.error("Login error:", err); // Debugging: Catch errors
      setError(err.response ? err.response.data.message : "Server error");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
