import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const categories = [
    {
      name: "Club",
      subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "sports"],
      image: "/images/clubs-image.jpg",
    },
    {
      name: "Academics",
      subcategories: ["First year", "Second year", "Third year", "Fourth year"],
      image: "/images/academic excellence.jpg",
    },
    {
      name: "Notices",
      subcategories: [],
      image: "/images/notice.jpg",
    },
    {
      name: "Skill Hub",
      subcategories: ["Hackathons", "Coding-Contests", "Training programmes"],
      image: "/images/skill-hub.jpg",
    },
    {
      name: "Events",
      subcategories: [],
      image: "/images/events-icon.jpg",
    },
    {
      name: "Placements",
      subcategories: [],
      image: "/images/placements.jpg",
    },
  ];

  return (
    <div className="outer-box-dashboard">
      <h1>Dashboard</h1>

      {/* CATEGORY SECTION */}
      <div className="category-container-dashboard">
        {categories.map((category) => (
          <div
            key={category.name}
            className="category-wrapper-dashboard"
            onMouseEnter={() => setActiveCategory(category.name)}
            onMouseLeave={() => setActiveCategory(null)}
          >
            <div className="category-icon-dashboard">
              <img
                src={category.image}
                alt={`${category.name} Icon`}
                className="category-image-dashboard"
              />
            </div>
            <div className="category-name-dashboard">{category.name}</div>
            {category.subcategories.length > 0 &&
              activeCategory === category.name && (
                <div className="sub-category-container-dashboard">
                  <ul>
                    {category.subcategories.map((subcategory, subIndex) => (
                      <li key={subIndex} className="subcategory-item-dashboard">
                        {subcategory === "RMF" ? (
                          <Link
                            to="/rmfpage"
                            className="subcategory-link-dashboard"
                          >
                            RMF
                          </Link>
                        ) : (
                          subcategory
                        )}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
          </div>
        ))}
      </div>

      {/* FEED SECTION */}
      <div className="feed-section-dashboard">
        <h2>Feed</h2>
        <div className="feed-grid-dashboard">
          {posts
            .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort posts by date
            .map((post, index) => (
              <div className="feed-item-dashboard" key={index}>
                <div className="feed-image-dashboard">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel
                      showThumbs={false}
                      infiniteLoop={true}
                      autoPlay={true}
                      interval={5000}
                      showStatus={false}
                    >
                      {post.media.map((media, idx) => (
                        <div key={idx}>
                          {media.type.includes("image") ? (
                            <img
                              src={media.url}
                              alt={`${post.title} ${idx}`}
                              style={{ objectFit: "cover", width: "100%" }}
                            />
                          ) : media.type.includes("video") ? (
                            <video
                              src={media.url}
                              controls
                              style={{ width: "100%" }}
                            />
                          ) : null}
                        </div>
                      ))}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-dashboard">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a
                      href={post.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="post-link-dashboard"
                    >
                      {post.link}
                    </a>
                  )}
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
