import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./Postspage.css";

const Postspage = ({ userEmail }) => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const [sortOrder, setSortOrder] = useState("desc");
  const [showFilterOptions, setShowFilterOptions] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [fullscreenImage, setFullscreenImage] = useState(null);
  // Fetch posts on component load
  useEffect(() => {
    const email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      return;
    }

    const fetchPosts = async () => {
      try {
        const url = `/postview/${encodeURIComponent(email)}`;
        const response = await axiosInstance.get(url);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [userEmail]); // Removed axiosInstance as a dependency

  // Delete post handler
  const filteredPosts = posts.filter((post) => {
      const searchString = searchTerm.toLowerCase();
      return Object.values(post).some((value) => {
        if (typeof value === "string") {
          return value.toLowerCase().includes(searchString);
        } else if (Array.isArray(value)) {
          return value.some(
            (item) =>
              typeof item === "string" && item.toLowerCase().includes(searchString)
          );
        }
        return false;
      });
    });
  
    const sortedPosts = filteredPosts.sort((a, b) => {
      return sortOrder === "desc"
        ? new Date(b.createdAt) - new Date(a.createdAt)
        : new Date(a.createdAt) - new Date(b.createdAt);
    });
  
    useEffect(() => {
      const handleKeyDown = (event) => {
        if (event.key === "Escape") {
          setFullscreenImage(null);
        }
      };
      window.addEventListener("keydown", handleKeyDown);
      return () => window.removeEventListener("keydown", handleKeyDown);
    }, [fullscreenImage]);
    
  const handleDelete = async (postId) => {
    try {
      console.log("Attempting to delete post with ID:", postId);

      const confirmation = window.confirm(
        "Are you sure you want to delete this post?"
      );
      if (!confirmation) return;

      await axiosInstance.delete(`/postview/${postId}`);
      alert("Post deleted successfully");

      // Update the state to reflect the deletion
      setPosts(posts.filter((post) => post._id !== postId));
    } catch (error) {
      console.error("Error deleting post:", error);
      alert("Failed to delete post.");
    }
  };

  return (
    <div className="posts-container-postpage">
      <h1>User Posts</h1>
      <div className="feed-header">
            <h2>Feed</h2>
            <input
              type="text"
              className="search-bar"
              placeholder="Search posts..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {searchTerm && (
              <button className="clear-search" onClick={() => setSearchTerm("")}>
                ×
              </button>
            )}
            <div className="filter-container">
              <button className="filter-button" onClick={() => setShowFilterOptions(!showFilterOptions)}>
                Filter ⌵
              </button>
              {showFilterOptions && (
                <div className="filter-dropdown">
                  <button
                    onClick={() => {
                      setSortOrder("desc");
                      setShowFilterOptions(false);
                    }}
                  >
                    Newest First
                  </button>
                  <button
                    onClick={() => {
                      setSortOrder("asc");
                      setShowFilterOptions(false);
                    }}
                  >
                    Oldest First
                  </button>
                </div>
              )}
            </div>
          </div>
      {/* New Post Button */}
      <button
        className="new-post-button-postpage"
        onClick={() => navigate("/Newpost")}
      >
        New Post
      </button>

      {/* Feed Section */}
      <div className="feed-section-postpage">
        <h2>Feed</h2>
        <div className="feed-grid-postpage">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts.map((post) => (
              <div className="feed-item-postpage" key={post._id}>
                <div className="feed-image-postpage">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel
                      showThumbs={false}
                      infiniteLoop={true}
                      autoPlay={true}
                      interval={5000}
                      showStatus={false}
                    >
                      {post.media.map((media, idx) => {
                        if (media.type.startsWith("image/")) {
                          return (
                            <div key={idx}>
                              <img
                                src={media.url}
                                alt={`${post.title} ${idx}`}
                                style={{ objectFit: "cover", width: "100%" }}
                              />
                            </div>
                          );
                        } else if (media.type === "application/pdf") {
                          return (
                            <div key={idx} className="pdf-container-postpage">
                              <iframe
                                src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "none" }}
                              ></iframe>
                            </div>
                          );
                        } else if (media.type === "video/mp4") {
                          return (
                            <div key={idx} className="video-container-postpage">
                              <video
                                src={media.url}
                                controls
                                style={{ width: "100%", height: "auto" }}
                              />
                            </div>
                          );
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-postpage">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a
                      href={post.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="post-link-postpage"
                    >
                      {post.link}
                    </a>
                  )}
                </div>
                <button
                  className="delete-post-button-postpage"
                  onClick={() => handleDelete(post._id)}
                >
                  Delete
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Postspage;
