import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DraftsPage.css";

const Draftspage = ({ userEmail }) => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  // Fetch drafts on component load
  useEffect(() => {
    const email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      return;
    }

    const fetchDrafts = async () => {
      try {
        const url = `/draftsview/${encodeURIComponent(email)}`;
        const response = await axiosInstance.get(url);
        setDrafts(response.data);
      } catch (error) {
        console.error("Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, [userEmail]);

  // Delete draft handler
  const handleDelete = async (draftId) => {
    try {
      const confirmation = window.confirm(
        "Are you sure you want to delete this draft?"
      );
      if (!confirmation) return;

      await axiosInstance.delete(`/draftview/${draftId}`);
      alert("Draft deleted successfully");

      // Update the state to reflect the deletion
      setDrafts(drafts.filter((draft) => draft._id !== draftId));
    } catch (error) {
      console.error("Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  // Edit draft handler
  const handleEdit = (draft) => {
    navigate("/Newpost", { state: { draft } });
  };

  return (
    <div className="drafts-container">
      <h1>User Drafts</h1>

      {/* New Draft Button */}
      <button
        className="new-draft-button"
        onClick={() => navigate("/Newpost")}
      >
        Create New Draft
      </button>

      {/* Drafts Section */}
      <div className="drafts-section">
        <h2>Drafts</h2>
        <div className="drafts-grid">
          {drafts.length === 0 ? (
            <p>No drafts available.</p>
          ) : (
            drafts.map((draft) => (
              <div className="draft-item" key={draft._id}>
                <div className="draft-image">
                  {draft.media.length > 0 && draft.media[0].url && (
                    <Carousel
                      showThumbs={false}
                      infiniteLoop={true}
                      autoPlay={true}
                      interval={5000}
                      showStatus={false}
                    >
                      {draft.media.map((media, idx) => {
                        if (media.type.startsWith("image/")) {
                          return (
                            <div key={idx}>
                              <img
                                src={media.url}
                                alt={`${draft.title} ${idx}`}
                                style={{ objectFit: "cover", width: "100%" }}
                              />
                            </div>
                          );
                        } else if (media.type === "application/pdf") {
                          return (
                            <div key={idx} className="pdf-container">
                              <iframe
                                src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "none" }}
                              ></iframe>
                            </div>
                          );
                        } else if (media.type === "video/mp4") {
                          return (
                            <div key={idx} className="video-container">
                              <video
                                src={media.url}
                                controls
                                style={{ width: "100%", height: "auto" }}
                              />
                            </div>
                          );
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="draft-content">
                  <h3>{draft.title}</h3>
                  <p>{draft.description}</p>
                  {draft.link && (
                    <a
                      href={draft.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="draft-link"
                    >
                      {draft.link}
                    </a>
                  )}
                </div>
                <div className="draft-buttons">
                  <button
                    className="edit-draft-button"
                    onClick={() => handleEdit(draft)}
                  >
                    Edit
                  </button>
                  <button
                    className="delete-draft-button"
                    onClick={() => handleDelete(draft._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Draftspage;
