import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css'; // Import the CSS file

const Header = ({ user }) => {
  const navigate = useNavigate();

  const openProfilePage = () => {
    if (user === 'student') {
      navigate('/profile/student');
    } else if (user === 'coordinator') {
      navigate('/profile/coordinator');
    }
  };

  return (
    <header className="header">
      <div className="logo-container">
        <img src="/favicon.png" alt="College Logo" className="logo" />
        <div className="college-text">
          <span className="college-name">SHRI VISHNU ENGINEERING COLLEGE FOR WOMEN(Autonomous)</span>
          <span className="college-location">Vishnupur, Bhimavaram, Andhra Pradesh 534202</span>
        </div>
      </div>
      <button onClick={openProfilePage} className="profile-button">
        Profile
      </button>
    </header>
  );
};

export default Header;
