import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = ({ user }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Remove role and email from localStorage on logout
    localStorage.removeItem("userRole");
    localStorage.removeItem("userEmail");

    // Redirect to login page
    navigate("/login");
  };

  return (
    <header>
      <div className="header-container">
        <div className="logo">
          <h1>College Portal</h1>
        </div>

        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            {user === "student" && (
              <>
                <li>
                  <Link to="/studentdashboard">Dashboard</Link>
                </li>
                <li>
                  <Link to="/profile/student">Profile</Link>
                </li>
              </>
            )}
            {user === "coordinator" && (
              <>
                <li>
                  <Link to="/coordinatordashboard">Dashboard</Link>
                </li>
                <li>
                  <Link to="/profile/coordinator">Profile</Link>
                </li>
              </>
            )}
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
