import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState(null);

  // Fetch posts from backend
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        if (!response.ok) throw new Error("Failed to fetch posts");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  // List of categories
  const categories = [
    {
      name: "Club",
      subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Sports"],
      image: "/images/clubs-image.jpg",
    },
    {
      name: "Academics",
      subcategories: ["First Year", "Second Year", "Third Year", "Fourth Year"],
      image: "/images/academic-excellence.jpg",
    },
    {
      name: "Notices",
      subcategories: [],
      image: "/images/notice.jpg",
    },
    {
      name: "Skill Hub",
      subcategories: ["Hackathons", "Coding-Contests", "Training Programs"],
      image: "/images/skill-hub.jpg",
    },
    {
      name: "Events",
      subcategories: [],
      image: "/images/events-icon.jpg",
    },
    {
      name: "Placements",
      subcategories: [],
      image: "/images/placements.jpg",
    },
  ];

  return (
    <div className="outer-box">
      {/* LEFT CONTAINER (Dashboard & Feed) */}
      <div className="left-container">
        {/* DASHBOARD SECTION */}
        <div className="dashboard-section">
          <h1>Dashboard</h1>
          <div className="dashboard-content">
            {/* Categories Section */}
            <div className="category-container">
              {categories.map((category) => (
                <div
                  key={category.name}
                  className="category-wrapper"
                  onMouseEnter={() => setActiveCategory(category.name)}
                  onMouseLeave={() => setActiveCategory(null)}
                >
                  <div className="category-icon">
                    <img
                      src={category.image}
                      alt={`${category.name} Icon`}
                      className="category-image"
                    />
                  </div>
                  <div className="category-name">{category.name}</div>
                  {category.subcategories.length > 0 && activeCategory === category.name && (
                    <div className="sub-category-container">
                      <ul>
                        {category.subcategories.map((subcategory, subIndex) => (
                          <li key={subIndex} className="subcategory-item">
                            {subcategory === "RMF" ? (
                              <Link to="/rmfpage" className="subcategory-link">
                                RMF
                              </Link>
                            ) : (
                              <span className="subcategory-link">{subcategory}</span>
                            )}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Analytics Section */}
            
          </div>
        </div>

        {/* FEED SECTION */}
        <div className="feed-section">
          <h2>Feed</h2>
          <div className="feed-grid">
            {posts
              .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
              .map((post, index) => (
                <div className="feed-item" key={index}>
                  <div className="feed-image">
                    {post.media.length > 0 && post.media[0].url && (
                      <Carousel
                        showThumbs={false}
                        infiniteLoop
                        autoPlay
                        interval={5000}
                        showStatus={false}
                      >
                        {post.media.map((media, idx) => (
                          <div key={idx}>
                            {media.type.includes("image") ? (
                              <img
                                src={media.url}
                                alt={post.title}
                                style={{ objectFit: "cover", width: "100%" }}
                              />
                            ) : media.type.includes("video") ? (
                              <video
                                src={media.url}
                                controls
                                style={{ width: "100%" }}
                              />
                            ) : null}
                          </div>
                        ))}
                      </Carousel>
                    )}
                  </div>
                  <div className="feed-content">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {post.link && (
                      <a
                        href={post.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="post-link"
                      >
                        {post.link}
                      </a>
                    )}
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
