import React, { useState, useEffect } from "react";
import axios from "axios";
import "./StudentProfile.css";

const StudentProfile = () => {
  const [profileImage, setProfileImage] = useState(
    "https://via.placeholder.com/100" // Default placeholder image
  );
  const [studentDetails, setStudentDetails] = useState(null);
  const [error, setError] = useState("");
  const [showOptions, setShowOptions] = useState(false);

  useEffect(() => {
    const email = localStorage.getItem("userEmail"); // Get email from localStorage
    if (email) {
      axios
        .get(`/api/student/${email}`)
        .then((response) => {
          const student = response.data;
          setStudentDetails(student);

          // If the profile image exists, set it
          if (student.image) {
            setProfileImage(`data:image/jpeg;base64,${student.image}`);
          }
        })
        .catch((err) => {
          setError("Failed to load student details.");
          console.error("Error:", err);
        });
    } else {
      setError("No email found");
    }
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfileImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
    setShowOptions(false);
  };

  const handleCaptureImage = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement("video");
      video.srcObject = stream;
      video.play();

      // Wait for the video stream to start
      await new Promise((resolve) => (video.onloadedmetadata = resolve));

      // Create a canvas to capture the video frame
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext("2d");
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Stop the video stream
      stream.getTracks().forEach((track) => track.stop());

      // Set the captured image as the profile image
      setProfileImage(canvas.toDataURL());
    } catch (error) {
      console.error("Error accessing the webcam:", error);
    }
    setShowOptions(false);
  };

  const handleDeleteImage = () => {
    setProfileImage("https://via.placeholder.com/100"); // Reset to default
    setShowOptions(false);
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-image-container">
          <img src={profileImage} alt="Profile" className="profile-image" />
          <button
            onClick={() => setShowOptions(!showOptions)}
            className="camera-icon-button"
          >
            <img
              src="/images/camera-icon.jpg" // Add the camera icon image to your project folder
              alt="Camera Icon"
              className="camera-icon"
            />
          </button>
        </div>
        {studentDetails && (
          <>
            <h2 className="profile-username">{studentDetails.name}</h2>
            <p className="profile-role">Student</p>
            <div className="profile-details">
              <p>
                <strong>Name:</strong> {studentDetails.name}
              </p>
              <p>
                <strong>Email:</strong> {studentDetails.email}
              </p>
              <p>
                <strong>Registration No:</strong> {studentDetails.studentid}
              </p>
              <p>
                <strong>Department:</strong> {studentDetails.branch}
              </p>
              <p>
                <strong>Phone Number:</strong> {studentDetails.phoneno}
              </p>
            </div>
          </>
        )}
        {showOptions && (
          <div className="options-menu">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="upload-button"
            />
            <div className="capture-delete-row">
              <button onClick={handleCaptureImage} className="capture-button">
                Capture Image
              </button>
              <button onClick={handleDeleteImage} className="delete-button">
                <img
                  src="/images/delete-icon.jpg" // Replace with the path to your dustbin icon image
                  alt="Delete"
                  className="dustbin-icon"
                />
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentProfile;
