import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const [draftId, setDraftId] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [existingMedia, setExistingMedia] = useState([]); // Keeping it in case media handling is needed

  const location = useLocation();
  const navigate = useNavigate();

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const draftIdFromParams = queryParams.get("draftId");

    if (draftIdFromParams) {
      setDraftId(draftIdFromParams);
      fetchDraftData(draftIdFromParams);
    }
  }, [location.search]);

  const fetchDraftData = async (id) => {
    try {
      const response = await fetch(`http://localhost:3000/draftsview/edit/${id}`);
      if (!response.ok) throw new Error("Failed to fetch draft");

      const draft = await response.json();
      setTitle(draft.title);
      setDescription(draft.description);
      setCategory(draft.category);
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setExistingMedia(draft.media || []);
    } catch (error) {
      console.error("Error fetching draft:", error);
      alert("Could not load draft. Please try again.");
    }
  };

  const resetForm = () => {
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setLink("");
    setExistingMedia([]);
    setDraftId(null);
    navigate("/drafts");
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const postData = {
      title,
      description,
      category,
      subCategory,
      status: isDraft ? "draft" : "posted",
      uploaderEmail: userEmail,
      link,
    };

    try {
      const url = draftId
        ? `http://localhost:3000/draftsview/update/${draftId}`
        : "http://localhost:3000/posts/create-post";

      const response = await fetch(url, {
        method: draftId ? "PUT" : "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(postData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Unknown error");
      }

      alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
      resetForm();
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Submission failed. Please try again.");
    }
  };

  const handleCancel = async () => {
    if (!draftId) {
      resetForm();
      return;
    }

    if (!window.confirm("Are you sure you want to delete this draft?")) return;

    try {
      const response = await fetch(`http://localhost:3000/draftsview/${draftId}`, { method: "DELETE" });
      if (!response.ok) throw new Error("Error deleting draft");

      alert("Draft deleted successfully!");
      resetForm();
    } catch (error) {
      console.error("Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  return (
    <div className="container">
      <h2>{draftId ? "Edit Draft" : "New Post"}</h2>
      <form>
        <input type="text" placeholder="Enter title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea placeholder="Write a description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="text" placeholder="Enter link (if any)" value={link} onChange={(e) => setLink(e.target.value)} />

        <select value={category} onChange={(e) => { setCategory(e.target.value); setSubCategory(""); }} required>
          <option value="" disabled>Select a category</option>
          {Object.keys(subCategoriesMap).map((key) => <option key={key} value={key}>{key}</option>)}
        </select>

        {category && subCategoriesMap[category]?.length > 0 && (
          <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)}>
            <option value="" disabled>Select a subcategory</option>
            {subCategoriesMap[category].map((sub, index) => <option key={index} value={sub.toLowerCase()}>{sub}</option>)}
          </select>
        )}

        <div className="button-container">
          <button type="button" onClick={() => handleSubmit(false)}>Submit Post</button>
          <button type="button" onClick={() => handleSubmit(true)}>Save as Draft</button>
          <button type="button" className="cancel-button" onClick={handleCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default NewPost;
