import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DraftsPage.css";

const Draftspage = ({ userEmail }) => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      console.log("No email found in props or localStorage.");
      return;
    }

    const fetchDrafts = async () => {
      try {
        const url = `/draftsview/${encodeURIComponent(email)}`;
        console.log("Fetching drafts from:", url);
        const response = await axiosInstance.get(url);
        console.log("Drafts fetched:", response.data);
        setDrafts(response.data);
      } catch (error) {
        console.error("Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, [userEmail]);

  const handleDelete = async (draftId) => {
    try {
      const confirmation = window.confirm(
        "Are you sure you want to delete this draft?"
      );
      if (!confirmation) return;

      console.log("Deleting draft with ID:", draftId);
      await axiosInstance.delete(`/draftsview/${draftId}`);
      alert("Draft deleted successfully");

      setDrafts(drafts.filter((draft) => draft._id !== draftId));
    } catch (error) {
      console.error("Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  const handleView = (draft) => {
    console.log("Navigating to /Newpost with draft:", draft);
    navigate("/Newpost", { state: { draft } });
  };

  return (
    <div className="drafts-container-draftspage">
      <h1>Drafts</h1>

      <div className="feed-section-draftspage">
        <h2>Your Drafts</h2>
        <div className="feed-grid-draftspage">
          {drafts.length === 0 ? (
            <p>No drafts available.</p>
          ) : (
            drafts.map((draft) => (
              <div className="feed-item-draftspage" key={draft._id}>
                <div className="feed-image-draftspage">
                  {draft.media.length > 0 && (
                    <Carousel
                      showThumbs={false}
                      infiniteLoop={true}
                      autoPlay={true}
                      interval={5000}
                      showStatus={false}
                    >
                      {draft.media.map((media, idx) => {
                        if (media.type.startsWith("image/")) {
                          return (
                            <div key={idx}>
                              <img
                                src={media.url}
                                alt={`${draft.title} ${idx}`}
                                style={{ objectFit: "cover", width: "100%" }}
                              />
                            </div>
                          );
                        } else if (media.type === "application/pdf") {
                          return (
                            <div key={idx} className="pdf-container-draftspage">
                              <iframe
                                src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "none" }}
                              ></iframe>
                            </div>
                          );
                        } else if (media.type === "video/mp4") {
                          return (
                            <div key={idx} className="video-container-draftspage">
                              <video
                                src={media.url}
                                controls
                                style={{ width: "100%", height: "auto" }}
                              />
                            </div>
                          );
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-draftspage">
                  <h3>{draft.title}</h3>
                  <p>{draft.description}</p>
                  {draft.link && (
                    <a
                      href={draft.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="draft-link-draftspage"
                    >
                      {draft.link}
                    </a>
                  )}
                </div>
                <div className="draft-buttons-draftspage">
                  <button
                    className="view-draft-button-draftspage"
                    onClick={() => handleView(draft)}
                  >
                    View/Edit
                  </button>
                  <button
                    className="delete-draft-button-draftspage"
                    onClick={() => handleDelete(draft._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Draftspage;
