import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from './api/axiosInstance';  // Make sure the path is correct
import "./Login.css";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send request to backend to authenticate
      const response = await axiosInstance.post("/login", { email, password });

      // Get the role from the response
      const { role, redirectTo } = response.data;
      
      // Call onLogin function passed from App.js to update user role
      onLogin(role);
      
      // Navigate based on the response
      navigate(redirectTo);
    } catch (err) {
      console.error("Login error:", err); // Log error to the console
      setError(err.response ? err.response.data.message : "Server error");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
};

export default Login;
