import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DraftsPage.css";

const Draftspage = ({ userEmail }) => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      console.log("No email found in props or localStorage.");
      return;
    }

    const fetchDrafts = async () => {
      try {
        const url = `/draftsview/${encodeURIComponent(email)}`;
        const response = await axiosInstance.get(url);
        setDrafts(response.data);
      } catch (error) {
        console.error("Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, [userEmail]);

  const handleDelete = async (draftId) => {
    try {
      if (!window.confirm("Are you sure you want to delete this draft?")) return;

      await axiosInstance.delete(`/draftsview/${draftId}`);
      alert("Draft deleted successfully");
      setDrafts(drafts.filter((draft) => draft._id !== draftId));
    } catch (error) {
      console.error("Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  const handleView = (draft) => {
    navigate(`/newpost?draftId=${draft._id}`, { state: { draft } });
  };

  return (
    <div className="drafts-container-draftspage">
      <h1>Drafts</h1>
      <div className="feed-section-draftspage">
        <h2>Your Drafts</h2>
        <div className="feed-grid-draftspage">
          {drafts.length === 0 ? (
            <p>No drafts available.</p>
          ) : (
            drafts.map((draft) => (
              <div className="feed-item-draftspage" key={draft._id}>
                <div className="feed-image-draftspage">
                  {draft.media.length > 0 && (
                    <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                      {draft.media.map((media, idx) => (
                        <div key={idx}>
                          {media.type.startsWith("image/") ? (
                            <img src={media.url} alt={`${draft.title} ${idx}`} style={{ objectFit: "cover", width: "100%" }} />
                          ) : media.type === "application/pdf" ? (
                            <iframe src={`https://docs.google.com/gview?url=${media.url}&embedded=true`} width="100%" height="500px" style={{ border: "none" }}></iframe>
                          ) : media.type === "video/mp4" ? (
                            <video src={media.url} controls style={{ width: "100%", height: "auto" }} />
                          ) : null}
                        </div>
                      ))}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-draftspage">
                  <h3>{draft.title}</h3>
                  <p>{draft.description}</p>
                  {draft.link && <a href={draft.link} target="_blank" rel="noopener noreferrer" className="draft-link-draftspage">{draft.link}</a>}
                </div>
                <div className="draft-buttons-draftspage">
                  <button onClick={() => handleView(draft)}>View/Edit</button>
                  <button onClick={() => handleDelete(draft._id)}>Delete</button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Draftspage;
