import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [sortOrder, setSortOrder] = useState("desc");
  const [activeCategory, setActiveCategory] = useState(null);
  const [showFilterOptions, setShowFilterOptions] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const filteredPosts = posts.filter((post) => {
    const searchString = searchTerm.toLowerCase();
    return Object.values(post).some((value) => {
      if (typeof value === "string") {
        return value.toLowerCase().includes(searchString);
      } else if (Array.isArray(value)) {
        return value.some(
          (item) => typeof item === "string" && item.toLowerCase().includes(searchString)
        );
      }
      return false;
    });
  });

  const sortedPosts = filteredPosts.sort((a, b) => {
    return sortOrder === "desc"
      ? new Date(b.createdAt) - new Date(a.createdAt)
      : new Date(a.createdAt) - new Date(b.createdAt);
  });

  const categories = [
    { name: "Club", subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "sports"], image: "/images/clubs-image.jpg" },
    { name: "Academics", subcategories: ["First year", "Second year", "Third year", "Fourth year"], image: "/images/academic excellence.jpg" },
    { name: "Notices", subcategories: [], image: "/images/notice.jpg" },
    { name: "Skill Hub", subcategories: ["Hackathons", "Coding-Contests", "Training programmes"], image: "/images/skill-hub.jpg" },
    { name: "Events", subcategories: [], image: "/images/events-icon.jpg" },
    { name: "Placements", subcategories: [], image: "/images/placements.jpg" },
  ];

  return (
    <div className="outer-box">
      <div className="left-container">
        <div className="dashboard-section">
          <h1>Dashboard</h1>
          <div className="category-container">
            {categories.map((category) => (
              <div key={category.name} className="category-wrapper">
                <div className="category-icon">
                  <img src={category.image} alt={`${category.name} Icon`} />
                </div>
                <div className="category-name">{category.name}</div>
                {category.subcategories.length > 0 && (
                  <div className="sub-category-container">
                    <ul>
                      {category.subcategories.map((subcategory, subIndex) => (
                        <li key={subIndex} className="subcategory-item">
                          {subcategory === "RMF" ? (
                            <Link to="/rmfpage" className="subcategory-link">RMF</Link>
                          ) : (
                            subcategory
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="feed-section">
          <div className="feed-header">
            <h2 className="feed-title">Feed</h2>
            <input
              type="text"
              className="search-bar"
              placeholder="Search posts..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {searchTerm && <button className="clear-search" onClick={() => setSearchTerm("")}>×</button>}
            <div className="filter-container">
              <button className="filter-button" onClick={() => setShowFilterOptions(!showFilterOptions)}>Filter ⌵</button>
              {showFilterOptions && (
                <div className="filter-dropdown">
                  <button onClick={() => { setSortOrder("desc"); setShowFilterOptions(false); }}>Newest First</button>
                  <button onClick={() => { setSortOrder("asc"); setShowFilterOptions(false); }}>Oldest First</button>
                </div>
              )}
            </div>
          </div>
          <div className="feed-grid">
            {sortedPosts.map((post, index) => (
              <div className="feed-item" key={index}>
                {post.media.length > 0 && post.media[0].url && (
                  <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                    {post.media.map((media, idx) => (
                      <div key={idx}>
                        {media.type.includes("image") ? (
                          <img src={media.url} alt={post.title} />
                        ) : media.type.includes("video") ? (
                          <video src={media.url} controls />
                        ) : null}
                      </div>
                    ))}
                  </Carousel>
                )}
                <div className="feed-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && <a href={post.link} target="_blank" rel="noopener noreferrer" className="post-link">{post.link}</a>}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="analytics-container">
        <h2>Analytics</h2>
        <img src="/images/powerbi-report.jpg" alt="Power BI Analytics" className="analytics-image" />
      </div>
    </div>
  );
};

export default DashboardContent;
