import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        console.log("Fetching posts...");
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        console.log("Fetched posts data:", data);
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const categories = []; // Define categories if needed

  const handlePdfClick = (url) => {
    window.open(url, "_blank"); // Opens PDF in a new tab
  };

  const handleVideoClick = (url) => {
    const videoElement = document.createElement("video");
    videoElement.src = url;
    videoElement.controls = true;
    videoElement.autoplay = true;
    videoElement.style.position = "fixed";
    videoElement.style.top = "50%";
    videoElement.style.left = "50%";
    videoElement.style.transform = "translate(-50%, -50%)";
    videoElement.style.zIndex = 9999;
    videoElement.style.width = "80vw";
    videoElement.style.height = "80vh";
    document.body.appendChild(videoElement);
    videoElement.requestFullscreen();
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>

      {/* CATEGORY SECTION */}
      <div className="category-container">
        {categories.map((category) => (
          <div
            key={category.name}
            className="category-wrapper"
            onMouseEnter={() => setActiveCategory(category.name)}
            onMouseLeave={() => setActiveCategory(null)}
          >
            <div className="category-icon">
              <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
            </div>
            <div className="category-name">{category.name}</div>
            {category.subcategories.length > 0 && activeCategory === category.name && (
              <div className="sub-category-container">
                <ul>
                  {category.subcategories.map((subcategory, subIndex) => (
                    <li key={subIndex} className="subcategory-item">
                      {subcategory === "RMF" ? (
                        <Link to="/rmfpage" className="subcategory-link">
                          RMF
                        </Link>
                      ) : (
                        subcategory
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* FEED SECTION */}
      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {posts
            .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort posts by date
            .map((post, index) => (
              <div className="feed-item" key={index}>
                <div className="feed-image">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel
                      showThumbs={false}
                      infiniteLoop={true}
                      autoPlay={true}
                      interval={3000}
                      showStatus={false}
                    >
                      {post.media.map((media, idx) => {
                        if (media.type === "image/jpeg" || media.type === "image/png" || media.type === "image/gif") {
                          return (
                            <div key={idx}>
                              <img src={media.url} alt={`${post.title} ${idx}`} />
                            </div>
                          );
                        } else if (media.type === "application/pdf") {
                          return (
                            <div key={idx} className="pdf-container">
                              <iframe
                                src={media.url}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "1px solid #ccc" }}
                              ></iframe>
                              <p
                                onClick={() => handlePdfClick(media.url)}
                                style={{ color: "blue", textDecoration: "underline", cursor: "pointer" }}
                              >
                                Open PDF in new tab
                              </p>
                            </div>
                          );
                        } else if (media.type === "video/mp4") {
                          return (
                            <div key={idx}>
                              <button onClick={() => handleVideoClick(media.url)}>Play Video</button>
                            </div>
                          );
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a href={post.link} target="_blank" rel="noopener noreferrer" className="post-link">
                      {post.link}
                    </a>
                  )}
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
