import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Login from "./Login";
import StudentDashboard from "./studentdashboard";
import CoordinatorDashboard from "./coordinatordashboard";
import StudentProfile from "./studentprofile";
import CoordinatorProfile from "./coordinatorprofile";
import Home from "./Home";
import DashboardContent from "./Dashboardcontent";
import Postspage from "./PostPage";
import Newpost from "./newpost";
import Header from "./Header";
import DraftsPage from "./DraftsPage";

const App = () => {
  const [userRole, setUserRole] = useState(null); // State for user role
  const [userEmail, setUserEmail] = useState(null); // State for user email
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Load user role and email from localStorage on component mount
    const storedRole = localStorage.getItem("userRole");
    const storedEmail = localStorage.getItem("userEmail");

    if (storedRole && storedEmail) {
      setUserRole(storedRole);
      setUserEmail(storedEmail);
    }
  }, []);

  useEffect(() => {
    if (userRole && userEmail) {
      // Save user role and email to localStorage whenever they change
      localStorage.setItem("userRole", userRole);
      localStorage.setItem("userEmail", userEmail);
    }
  }, [userRole, userEmail]);

  // Exclude Header for these routes
  const excludeHeaderRoutes = ["/", "/login"].map((route) => route.toLowerCase());

  const handleLogin = (role, email) => {
    setUserRole(role); // Set the role dynamically
    setUserEmail(email); // Set the email dynamically
    if (role === "student") {
      navigate("/studentdashboard");
    } else if (role === "coordinator") {
      navigate("/coordinatordashboard");
    }
  };

  return (
    <>
      {userRole && !excludeHeaderRoutes.includes(location.pathname.toLowerCase()) && <Header user={userRole} />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/studentdashboard" element={<StudentDashboard email={userEmail} />} />
        <Route path="/coordinatordashboard" element={<CoordinatorDashboard email={userEmail} />} />
        <Route path="/profile/student" element={<StudentProfile email={userEmail} />} />
        <Route path="/profile/coordinator" element={<CoordinatorProfile email={userEmail} />} />
        <Route path="/dashboardcontent" element={<DashboardContent />} />
        <Route path="/postspage" element={<Postspage />} />
        <Route path="/newpost" element={<Newpost />} />
        <Route path="/draftspage" element={<DraftsPage />} />
        <Route path="*" element={<div>404 - Page Not Found</div>} />
      </Routes>
    </>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
