import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaSearch, FaFilter } from "react-icons/fa";

const DashboardContent = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showFilter, setShowFilter] = useState(false);

  const categories = [
    { name: "Tech", image: "/images/tech.png", subcategories: ["AI", "Blockchain"] },
    { name: "Health", image: "/images/health.png", subcategories: ["Wellness", "Nutrition"] },
    { name: "Finance", image: "/images/finance.png", subcategories: ["Stocks", "Crypto"] },
  ];

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="outer-box">
      {/* Left Container */}
      <div className="left-container">
        <section className="dashboard-section">
          <h2>Dashboard</h2>
          <p>Welcome! Explore the latest insights.</p>
        </section>

        <section className="feed-section">
          <div className="feed-header">
            <h3>Latest Feed</h3>

            {/* Search Bar */}
            <div className="search-container">
              <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearch}
                className="search-bar"
              />
              {searchTerm && (
                <button onClick={() => setSearchTerm("")} className="clear-search">
                  ✖
                </button>
              )}
            </div>

            {/* Filter Button */}
            <div className="filter-container">
              <button onClick={() => setShowFilter(!showFilter)} className="filter-button">
                <FaFilter /> Filter
              </button>

              {showFilter && (
                <div className="filter-dropdown">
                  <button onClick={() => alert("Filter Applied: Category")}>Category</button>
                  <button onClick={() => alert("Filter Applied: Date")}>Date</button>
                  <button onClick={() => alert("Filter Applied: Popular")}>Popular</button>
                </div>
              )}
            </div>
          </div>

          <div className="category-container">
            {categories.map((category, index) => (
              <div key={index} className="category-wrapper">
                <div className="category-icon">
                  <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
                </div>
                <div className="category-name">{category.name}</div>

                {category.subcategories.length > 0 && (
                  <div className="sub-category-container">
                    <ul>
                      {category.subcategories.map((subcategory, subIndex) => (
                        <li key={subIndex} className="subcategory-item">
                          {subcategory === "RMF" ? (
                            <Link to="/rmfpage" className="subcategory-link">RMF</Link>
                          ) : (
                            subcategory
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default DashboardContent;
