// newposts.js
import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const draft = location.state?.draft || {};

  const [title, setTitle] = useState(draft.title || "");
  const [description, setDescription] = useState(draft.description || "");
  const [category, setCategory] = useState(draft.category || "");
  const [subCategory, setSubCategory] = useState(draft.subCategory || "");
  const [link, setLink] = useState(draft.link || "");
  const [files, setFiles] = useState([]);
  const [filePreviews, setFilePreviews] = useState(draft.media || []);

  useEffect(() => {
    if (draft.media) {
      setFilePreviews(draft.media);
    }
  }, [draft]);

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);

    if (files.length > 0) {
      files.forEach((file) => {
        formData.append("media", file);
      });
    }

    try {
      const endpoint = draft._id
        ? `http://localhost:3000/posts/update-post/${draft._id}`
        : "http://localhost:3000/posts/create-post";

      const response = await fetch(endpoint, {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
        navigate("/");
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>{draft._id ? "Edit Draft" : "New Post"}</h2>
      <form>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
        <button type="button" onClick={() => handleSubmit(false)}>Submit Post</button>
        <button type="button" onClick={() => handleSubmit(true)}>Save as Draft</button>
      </form>
    </div>
  );
};

export default NewPost;
