import React from "react";
import { useNavigate } from "react-router-dom";
import "./Header.css"; // Import the CSS file

const Header = ({ user, email }) => {
  const navigate = useNavigate();

  // Function to open the profile page based on user type and email
  const openProfilePage = () => {
    if (user === "student") {
      navigate(`/profile/student?email=${email}`); // Pass email as query parameter
    } else if (user === "coordinator") {
      navigate(`/profile/coordinator?email=${email}`); // Pass email as query parameter
    }
  };

  // Function to navigate to the home page (dashboard content)
  const openHomePage = () => {
    navigate("/dashboardcontent"); // Make sure this route exists
  };

  return (
    <header className="header">
      <div className="logo-container">
        <img src="/images/favicon.png" alt="College Logo" className="logo" />
        <div className="college-textprofile">
          <span className="college-nameprofile">Shri Vishnu Engineering College</span>
          <span className="college-subnameprofile">For Women (Autonomous)</span>
          <span className="college-locationprofile">
            Vishnupur, Bhimavaram, Andhra Pradesh 534202
          </span>
        </div>
      </div>
      <div className="icon-container">
        <img
          src="/images/home.jpg"
          alt="Home Icon"
          onClick={openHomePage}
          className="home-icon-button"
        />
        <img
          src="/images/profile.jpg"
          alt="Profile Icon"
          onClick={openProfilePage}
          className="profile-icon-button"
        />
      </div>
    </header>
  );
};

export default Header;
