import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Login from "./Login";
import StudentDashboard from "./studentdashboard";
import CoordinatorDashboard from "./coordinatordashboard";
import StudentProfile from "./studentprofile";
import CoordinatorProfile from "./coordinatorprofile";
import Home from "./Home";
import DashboardContent from "./Dashboardcontent";
import Postspage from "./Postspage";
import Newpost from "./Newpost";
import Header from "./Header";
import DraftsPage from "./DraftsPage"; // Import DraftsPage
import RMFPage from "./RMFpage"; // Import RMFPage

const App = () => {
  const [userRole, setUserRole] = useState(null); // State for user role
  const navigate = useNavigate();
  const location = useLocation();

  // Define routes where the header should not be displayed
  const excludeHeaderRoutes = ["/", "/login"];

  // Determine whether to show the header based on the current route
  const shouldShowHeader = !excludeHeaderRoutes.includes(location.pathname.toLowerCase());

  // Handle login logic based on user role
  const handleLogin = (role) => {
    setUserRole(role); // Set the role dynamically
    if (role === "student") {
      navigate("/studentdashboard");
    } else if (role === "coordinator") {
      navigate("/coordinatordashboard");
    }
  };

  // Handle logout
  const handleLogout = () => {
    setUserRole(null); // Clear the user role
    navigate("/login");
  };

  return (
    <>
      {shouldShowHeader && <Header user={userRole} onLogout={handleLogout} />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/studentdashboard" element={<StudentDashboard />} />
        <Route path="/coordinatordashboard" element={<CoordinatorDashboard />} />
        <Route path="/profile/student" element={<StudentProfile />} />
        <Route path="/profile/coordinator" element={<CoordinatorProfile />} />
        <Route path="/dashboardcontent" element={<DashboardContent />} />
        <Route path="/postspage" element={<Postspage />} />
        <Route path="/newpost" element={<Newpost />} />
        <Route path="/draftspage" element={<DraftsPage />} />
        <Route path="/rmfpage" element={<RMFPage />} />
        <Route path="*" element={<div>404 - Page Not Found</div>} />
      </Routes>
    </>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
