import React from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import logo from 'college_activity/public/favicon.png'; // Adjust the path to your logo image

const HomePage = () => {
  const navigate = useNavigate(); // React Router hook for navigation

  const handleLoginClick = () => {
    navigate("/Login"); // Redirect to Login page
  };

  return (
    <div className="container">
      {/* Left Section - Logo and Text */}
      <div className="left-section">
        <div className="logo-container">
          <img src={logo} alt="College Logo" className="college-logo" />
          <h1 className="college-title">College Chronicle</h1>
        </div>
        <h2 className="welcome-message">
          Welcome to College Chronicle at Shri Vishnu Engineering College for Women
        </h2>
        <p className="intro-text">
          Experience the vibrant life of our campus, where education meets opportunities.
          Learn, grow, and achieve your dreams. Join us on this exciting journey!
        </p>
        <button className="login-button" onClick={handleLoginClick}>
          Login
        </button>
      </div>

      {/* Right Section - Image Carousel */}
      <div className="right-section">
        <Carousel
          showThumbs={false}
          infiniteLoop={true}
          autoPlay={true}
          interval={3000}
          showStatus={false}
        >
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 1" />
            <p className="legend">Campus Life</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 2" />
            <p className="legend">Events</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 3" />
            <p className="legend">Learning Spaces</p>
          </div>
        </Carousel>
      </div>
    </div>
  );
};

export default HomePage;
