import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./Postspage.css";

const Postspage = ({ userEmail }) => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  // Create an Axios instance with base URL
  const axiosInstance = axios.create({
    baseURL: "http://localhost:3001/postview", // Ensure this matches your backend server
  });

  // Fetch posts on component load
  useEffect(() => {
    const email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      return;
    }

    const fetchPosts = async () => {
      try {
        const url = `/${encodeURIComponent(email)}`;
        const response = await axiosInstance.get(url);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [userEmail]);

  // Delete post handler
  const handleDelete = async (postId) => {
    try {
      console.log("Attempting to delete post with ID:", postId);
      console.log("DELETE URL:", `/${postId}`);

      const confirmation = window.confirm("Are you sure you want to delete this post?");
      if (!confirmation) return;

      await axiosInstance.delete(`/${postId}`);
      alert("Post deleted successfully");

      // Update the state to reflect the deletion
      setPosts(posts.filter((post) => post._id !== postId));
    } catch (error) {
      console.error("Error deleting post:", error);
      alert("Failed to delete post.");
    }
  };

  return (
    <div className="posts-container">
      <h1>User Posts</h1>

      {/* New Post Button */}
      <button className="new-post-button" onClick={() => navigate("/Newpost")}>
        New Post
      </button>

      {/* Feed Section */}
      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts.map((post) => (
              <div className="feed-item" key={post._id}>
                <div className="feed-image">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel showThumbs={false} infiniteLoop={true} autoPlay={true} interval={5000} showStatus={false}>
                      {post.media.map((media, idx) => {
                        if (media.type.startsWith("image/")) {
                          return (
                            <div key={idx}>
                              <img src={media.url} alt={`${post.title} ${idx}`} style={{ objectFit: "cover", width: "100%" }} />
                            </div>
                          );
                        } else if (media.type === "application/pdf") {
                          return (
                            <div key={idx} className="pdf-container">
                              <iframe
                                src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "none" }}
                              ></iframe>
                            </div>
                          );
                        } else if (media.type === "video/mp4") {
                          return (
                            <div key={idx} className="video-container">
                              <video src={media.url} controls style={{ width: "100%", height: "auto" }} />
                            </div>
                          );
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a href={post.link} target="_blank" rel="noopener noreferrer" className="post-link">
                      {post.link}
                    </a>
                  )}
                </div>
                <button className="delete-post-button" onClick={() => handleDelete(post._id)}>
                  Delete
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Postspage;
