import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  // const [activeCategory, setActiveCategory] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        setPosts(data);
        setFilteredPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const categories = [
    {
      name: "Club",
      subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "sports"],
      image: "/images/clubs-image.jpg",
    },
    {
      name: "Academics",
      subcategories: ["First year", "Second year", "Third year", "Fourth year"],
      image: "/images/academic-excellence.jpg",
    },
    {
      name: "Notices",
      subcategories: [],
      image: "/images/notice.jpg",
    },
    {
      name: "Skill Hub",
      subcategories: ["Hackathons", "Coding-Contests", "Training programmes"],
      image: "/images/skill-hub.jpg",
    },
    {
      name: "Events",
      subcategories: [],
      image: "/images/events-icon.jpg",
    },
    {
      name: "Placements",
      subcategories: [],
      image: "/images/placements.jpg",
    },
  ];

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    const filtered = posts.filter(
      (post) =>
        post.title.toLowerCase().includes(term) ||
        post.description.toLowerCase().includes(term)
    );
    setFilteredPosts(filtered);
  };

  const handleCategoryFilter = (category) => {
    const filtered = posts.filter((post) => post.category === category);
    setFilteredPosts(filtered);
  };

  const handleSubcategoryClick = (subcategory) => {
    navigate(`/rmfpage?subcategory=${subcategory}`);
  };

  return (
    <div className="outer-box-dashboardcontent">
      <div className="left-container-dashboardcontent">
        <div className="dashboard-section-dashboardcontent">
          <h1>Dashboard</h1>
          <div className="dashboard-content-dashboardcontent">
            <div className="category-container-dashboardcontent">
              {categories.map((category) => (
                <div
                  key={category.name}
                  className="category-wrapper-dashboardcontent"
                  onClick={() => handleCategoryFilter(category.name)}
                >
                  <div className="category-icon-dashboardcontent">
                    <img
                      src={category.image}
                      alt={`${category.name} Icon`}
                      className="category-image-dashboardcontent"
                    />
                  </div>
                  <div className="category-name-dashboardcontent">{category.name}</div>
                  {category.subcategories.length > 0 && (
                    <div className="sub-category-container-dashboardcontent">
                      <ul>
                        {category.subcategories.map((subcategory, subIndex) => (
                          <li key={subIndex} className="subcategory-item-dashboardcontent">
                            <button
                              className="subcategory-link-dashboardcontent"
                              onClick={() => handleSubcategoryClick(subcategory)}
                            >
                              {subcategory}
                            </button>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="feed-section-postpage-dashboardcontent">
          <h2>Feed</h2>
          <input
            type="text"
            placeholder="Search posts..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-input-dashboardcontent"
          />

          <div className="feed-grid-postpage-dashboardcontent">
            {filteredPosts.length === 0 ? (
              <p>No posts available.</p>
            ) : (
              filteredPosts.map((post) => (
                <div className="feed-item-postpage-dashboardcontent" key={post._id}>
                  <div className="feed-image-postpage-dashboardcontent">
                    {post.media.length > 0 && post.media[0].url && (
                      <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                        {post.media.map((media, idx) => {
                          if (media.type.startsWith("image/")) {
                            return <img key={idx} src={media.url} alt={`${post.title}`} />;
                          } else if (media.type === "application/pdf") {
                            return (
                              <iframe
                                key={idx}
                                src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                title="PDF Viewer"
                                width="100%"
                                height="500px"
                                style={{ border: "none" }}
                              ></iframe>
                            );
                          } else if (media.type === "video/mp4") {
                            return <video key={idx} src={media.url} controls style={{ width: "100%" }} />;
                          }
                          return null;
                        })}
                      </Carousel>
                    )}
                  </div>
                  <div className="feed-content-postpage-dashboardcontent">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {post.link && (
                      <a href={post.link} target="_blank" rel="noopener noreferrer">
                        {post.link}
                      </a>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
