import React, { useEffect, useState } from "react";
import axios from "axios";

const NewPost = ({ draft, userEmail }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [files, setFiles] = useState([]);
  const [filePreviews, setFilePreviews] = useState([]);
  const [draftId, setDraftId] = useState(null);

  useEffect(() => {
    if (draft) {
      console.log("Draft loaded:", draft); // Log the entire draft object
      setTitle(draft.title || "");
      setDescription(draft.description || "");
      setCategory(draft.category || "");
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setFilePreviews(draft.media || []);
      console.log("File previews set from draft:", draft.media); // Log the media URLs
      setDraftId(draft._id || null);
    }
  }, [draft]);

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles([...files, ...selectedFiles]);
    console.log("Selected files:", selectedFiles);
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    console.log("Submitting post with the following data:");
    console.log("Title:", title);
    console.log("Description:", description);
    console.log("Category:", category);
    console.log("Files to upload:", files);
    console.log("Draft ID (if any):", draftId);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);

    if (files.length > 0) {
      files.forEach((file) => {
        console.log("Uploading file:", file.name);
        formData.append("media", file);
      });
    }

    try {
      const endpoint = draftId
        ? `/api/posts/update-post/${draftId}`
        : "/api/posts/create-post";
      const method = draftId ? "patch" : "post";

      const response = await axios[method](endpoint, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Post submitted successfully:", response.data);
      alert("Post submitted successfully!");
    } catch (error) {
      console.error("Error while submitting post:", error);
      alert("Failed to submit post.");
    }
  };

  return (
    <div>
      <h1>{draftId ? "Edit Draft" : "Create New Post"}</h1>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <input
        type="text"
        placeholder="Subcategory"
        value={subCategory}
        onChange={(e) => setSubCategory(e.target.value)}
      />
      <input
        type="text"
        placeholder="Link"
        value={link}
        onChange={(e) => setLink(e.target.value)}
      />
      <input type="file" multiple onChange={handleFileChange} />
      <button onClick={() => handleSubmit(true)}>Save as Draft</button>
      <button onClick={() => handleSubmit(false)}>Submit</button>
    </div>
  );
};

export default NewPost;
