import React, { useState } from "react";
import "./Newpost.css";

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]); // Array to store file names
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");

  // Subcategories mapping
  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: [
      "First Year",
      "Second Year",
      "Third Year",
      "Fourth Year",
    ],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  // Handle file selection (multiple files)
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setFileNames((prevFileNames) => [
      ...prevFileNames,
      ...files.map((file) => file.name),
    ]);
  };

  // Reset form fields
  const resetForm = () => {
    setFileNames([]);
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
  };

  // Handle post submission
  const handlePost = () => {
    if (!title || !description || !category || fileNames.length === 0) {
      alert("Please fill all the fields and upload at least one file!");
      return;
    }
    alert("Post submitted successfully!");
    console.log({
      title,
      description,
      category,
      subCategory,
      fileNames,
    });
    resetForm();
  };

  // Handle saving the post as a draft
  const handleSaveDraft = () => {
    if (!title || !description || !category) {
      alert("Please fill in the title, description, and category to save as a draft!");
      return;
    }
    alert("Post saved as draft!");
    console.log({
      title,
      description,
      category,
      subCategory,
      fileNames,
    });
    resetForm();
  };

  // Remove file from the list
  const handleRemoveFile = (fileName) => {
    setFileNames((prevFileNames) =>
      prevFileNames.filter((name) => name !== fileName)
    );
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        {/* File upload section */}
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0
              ? fileNames.join(", ") // Display all uploaded file names
              : "Click to browse or drop files here"}
          </label>
          <input
            type="file"
            id="fileInput"
            onChange={handleFileChange}
            multiple // Allow multiple files
            style={{ display: "none" }}
          />
        </div>

        {/* Title Input */}
        <input
          type="text"
          placeholder="Enter title for the file"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        {/* Description Input */}
        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        {/* Category Selection */}
        <select
          value={category}
          onChange={(e) => {
            setCategory(e.target.value);
            setSubCategory(""); // Reset subcategory when category changes
          }}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          <option value="events">Events</option>
          <option value="clubs">Clubs</option>
          <option value="academics">Academics</option>
          <option value="notices">Notices</option>
          <option value="skillhub">Skillhub</option>
          <option value="placements">Placements</option>
        </select>

        {/* Subcategory Selection */}
        {category && subCategoriesMap[category]?.length > 0 && (
          <select
            value={subCategory}
            onChange={(e) => setSubCategory(e.target.value)}
            required
          >
            <option value="" disabled>
              Select a subcategory
            </option>
            {subCategoriesMap[category].map((sub, index) => (
              <option key={index} value={sub.toLowerCase()}>
                {sub}
              </option>
            ))}
          </select>
        )}

        {/* Buttons */}
        <div className="button-container">
          <button
            type="button"
            className="save-draft"
            onClick={handleSaveDraft}
          >
            Save as Draft
          </button>
          <button type="button" className="post" onClick={handlePost}>
            Post
          </button>
        </div>

        {/* Display the list of uploaded files with Remove option */}
        {fileNames.length > 0 && (
          <div className="uploaded-files">
            <h4>Uploaded Files:</h4>
            <ul>
              {fileNames.map((fileName, index) => (
                <li key={index}>
                  {fileName}
                  <span
                    onClick={() => handleRemoveFile(fileName)}
                    className="remove-file"
                  >
                    ×
                  </span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </form>
    </div>
  );
};

export default NewPost;
