import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import "./DraftsPage.css";

const DraftsPage = ({ userEmail }) => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let email = userEmail || localStorage.getItem("userEmail");
    console.log("🔍 Fetching drafts for:", email);

    if (!email) {
      console.error("⚠️ No user email available, skipping fetch.");
      return;
    }

    const fetchDrafts = async () => {
      try {
        const response = await axiosInstance.get(`/draftsview/${encodeURIComponent(email)}`);
        console.log("✅ Drafts fetched:", response.data);
        setDrafts(response.data);
      } catch (error) {
        console.error("❌ Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, [userEmail]);

  const handleDeleteDraft = async (draftId) => {
    try {
      const confirmation = window.confirm("Are you sure you want to delete this draft?");
      if (!confirmation) return;

      await axiosInstance.delete(`/draftsview/${draftId}`);
      alert("Draft deleted successfully");
      
      // Update state after deletion
      setDrafts(drafts.filter((draft) => draft._id !== draftId));
    } catch (error) {
      console.error("❌ Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  const handleViewDraft = (draft) => {
    navigate("/Newpost", {
      state: { draft: draft }, // Pass draft data to the new page
    });
  };

  return (
    <div className="drafts-container">
      <h1>Saved Drafts</h1>

      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {drafts.length === 0 ? (
            <p>No drafts available.</p>
          ) : (
            drafts
              .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort newest first
              .map((draft) => (
                <div key={draft._id} className="feed-item">
                  {draft.media && draft.media.length > 0 && (
                    <img
                      className="feed-image-item"
                      src={draft.media[0].url}
                      alt={draft.title}
                    />
                  )}
                  <div className="feed-content">
                    <h3>{draft.title}</h3>
                    <p>{draft.description}</p>
                    <button
                      className="view-draft-button"
                      onClick={() => handleViewDraft(draft)}
                    >
                      View
                    </button>
                    <button
                      className="delete-draft-button"
                      onClick={() => handleDeleteDraft(draft._id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))
          )}
        </div>
      </div>
    </div>
  );
};

export default DraftsPage;
