import React, { createContext, useState } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [userEmail, setUserEmail] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userDetails, setUserDetails] = useState(null); // New state to store user details

  const login = (email, role, details) => {
    setUserEmail(email);
    setUserRole(role);
    setUserDetails(details); // Store user details when logging in
  };

  const logout = () => {
    setUserEmail(null);
    setUserRole(null);
    setUserDetails(null); // Clear user details on logout
  };

  return (
    <AuthContext.Provider value={{ userEmail, userRole, userDetails, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
