// Header.js
import React from 'react';
import { useHistory, useLocation } from 'react-router-dom';

const Header = () => {
  const history = useHistory();
  const location = useLocation();
  
  const user = location.pathname.split('/')[2]; // Extract user from the URL

  const openProfilePage = () => {
    // Navigate to the correct profile page based on the user
    if (user === 'user1') {
      history.push('/profile/user1');
    } else if (user === 'user2') {
      history.push('/profile/user2');
    }
  };

  return (
    <header style={headerStyle}>
      <div style={logoContainerStyle}>
        <img src="logo.png" alt="College Logo" style={logoStyle} />
        <span style={collegeNameStyle}>College Name</span>
      </div>
      <button onClick={openProfilePage} style={profileButtonStyle}>
        Profile
      </button>
    </header>
  );
};

const headerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  backgroundColor: '#2c3e50',
  color: 'white',
  padding: '10px 20px',
};

const logoContainerStyle = {
  display: 'flex',
  alignItems: 'center',
};

const logoStyle = {
  width: '40px',
  height: '40px',
  marginRight: '10px',
};

const collegeNameStyle = {
  fontSize: '20px',
};

const profileButtonStyle = {
  padding: '10px 15px',
  backgroundColor: '#2980b9',
  border: 'none',
  color: 'white',
  cursor: 'pointer',
};

export default Header;
