import React from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const HomePage = () => {
  const navigate = useNavigate(); // React Router hook for navigation

  const handleLoginClick = () => {
    navigate("/Login"); // Redirect to Login page
  };

  return (
    <div className="containerhome">
      {/* Left Section - Logo and Text */}
      <div className="left-sectionhome">
        <div className="logo-containerhome">
          <img src="/favicon.png" alt="College Logo" className="logohome" />
          <h1 className="college-titlehome">College Chronicle</h1>
        </div>
        
        <p className="intro-texthome">
          Experience the vibrant life of our campus, where education meets opportunities.
          Learn, grow, and achieve your dreams. Join us on this exciting journey!
        </p>
        <button className="login-buttonhome" onClick={handleLoginClick}>
          Login
        </button>
      </div>

      {/* Right Section - Image Carousel */}
      <div className="right-sectionhome">
        <Carousel
          showThumbs={false}
          infiniteLoop={true}
          autoPlay={true}
          interval={3000}
          showStatus={false}
        >
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 1" />
            <p className="legendhome">Campus Life</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 2" />
            <p className="legendhome">Events</p>
          </div>
          <div>
            <img src="https://via.placeholder.com/800x400" alt="Campus 3" />
            <p className="legendhome">Learning Spaces</p>
          </div>
        </Carousel>
      </div>
    </div>
  );
};

export default HomePage;
