import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const [draftId, setDraftId] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [existingMedia, setExistingMedia] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const draftIdFromParams = queryParams.get("draftId");

    if (draftIdFromParams) {
      setDraftId(draftIdFromParams);
      fetchDraftData(draftIdFromParams);
    }
  }, [location.search]);

  const fetchDraftData = async (id) => {
    try {
      const response = await fetch(`http://localhost:3000/draftsview/edit/${id}`);
      if (!response.ok) throw new Error("Failed to fetch draft");

      const draft = await response.json();
      setTitle(draft.title);
      setDescription(draft.description);
      setCategory(draft.category);
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setExistingMedia(draft.media || []);
    } catch (error) {
      alert("Could not load draft. Please try again.");
    }
  };

  const handleSubmit = async (isDraft) => {
    if (!title || !description || !category) {
      alert("Please fill all required fields!");
      return;
    }

    const postData = {
      title,
      description,
      category,
      subCategory,
      status: isDraft ? "draft" : "posted",
      uploaderEmail: userEmail,
      link,
    };

    try {
      let url = draftId
        ? `http://localhost:3000/draftsview/update/${draftId}`
        : "http://localhost:3000/posts/create-post";
      let method = draftId ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(postData),
      });

      if (!response.ok) {
        throw new Error("Failed to submit post");
      }

      alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");

      if (!isDraft && draftId) {
        await fetch(`http://localhost:3000/draftsview/delete/${draftId}`, { method: "DELETE" });
      }

      navigate("/draftspage"); // Redirect back to drafts page
    } catch (error) {
      alert("Submission failed. Please try again.");
    }
  };

  const handleCancel = async () => {
    if (!draftId) {
      navigate("/draftspage");
      return;
    }

    if (window.confirm("Are you sure you want to delete this draft?")) {
      try {
        await fetch(`http://localhost:3000/draftsview/delete/${draftId}`, { method: "DELETE" });
        alert("Draft deleted successfully.");
        navigate("/draftspage");
      } catch (error) {
        alert("Failed to delete draft. Please try again.");
      }
    }
  };

  return (
    <div>
      <h2>{draftId ? "Edit Draft" : "New Post"}</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
      <input value={category} onChange={(e) => setCategory(e.target.value)} placeholder="Category" required />
      <input value={subCategory} onChange={(e) => setSubCategory(e.target.value)} placeholder="Sub-Category" />
      <input value={link} onChange={(e) => setLink(e.target.value)} placeholder="Link" />
      <button onClick={() => handleSubmit(false)}>Submit Post</button>
      <button onClick={() => handleSubmit(true)}>Save as Draft</button>
      <button onClick={handleCancel}>Cancel</button>
    </div>
  );
};

export default NewPost;
