import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./Postspage.css";

const Postspage = ({ userEmail }) => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let email = userEmail || localStorage.getItem("userEmail");

    if (!email) {
      return;
    }

    const fetchPosts = async () => {
      try {
        const url = `/postview/${encodeURIComponent(email)}`;
        const response = await axiosInstance.get(url);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [userEmail]);

  return (
    <div className="posts-container">
      <h1>User Posts</h1>

      {/* New Post Button */}
      <button className="new-post-button" onClick={() => navigate("/Newpost")}>
        New Post
      </button>

      {/* Feed Section */}
      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts
              .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort by newest first
              .map((post, index) => (
                <div className="feed-item" key={index}>
                  <div className="feed-image">
                    {post.media.length > 0 && post.media[0].url && (
                      <Carousel
                        showThumbs={false}
                        infiniteLoop={true}
                        autoPlay={true}
                        interval={5000} // Adjusted interval to slow down autoplay
                        showStatus={false}
                      >
                        {post.media.map((media, idx) => {
                          if (
                            media.type === "image/jpeg" ||
                            media.type === "image/png" ||
                            media.type === "image/gif"
                          ) {
                            return (
                              <div key={idx}>
                                <img
                                  src={media.url}
                                  alt={`${post.title} ${idx}`}
                                  style={{ objectFit: "cover", width: "100%" }}
                                />
                              </div>
                            );
                          } else if (media.type === "application/pdf") {
                            return (
                              <div key={idx} className="pdf-container">
                                <iframe
                                  src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                                  title="PDF Viewer"
                                  width="100%"
                                  height="500px"
                                  style={{ border: "none" }}
                                ></iframe>
                              </div>
                            );
                          } else if (media.type === "video/mp4") {
                            return (
                              <div key={idx} className="video-container">
                                <video
                                  src={media.url}
                                  controls
                                  style={{ width: "100%", height: "auto" }}
                                />
                              </div>
                            );
                          }
                          return null;
                        })}
                      </Carousel>
                    )}
                  </div>
                  <div className="feed-content">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {post.link && (
                      <a
                        href={post.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="post-link"
                      >
                        {post.link}
                      </a>
                    )}
                  </div>
                </div>
              ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Postspage;
