import React, { useState } from "react";
import "./Newpost.css";

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [files, setFiles] = useState([]);

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
    setFileNames((prevNames) => [...prevNames, ...selectedFiles.map((file) => file.name)]);
  };

  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFileNames((prevNames) => prevNames.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    setFileNames([]);
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setFiles([]);
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted"); // Status for draft or post
    files.forEach((file) => formData.append("media", file));

    try {
      const response = await fetch("http://localhost:3000/posts/create-post", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const message = isDraft ? "Draft saved successfully!" : "Post submitted successfully!";
        alert(message);
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0
              ? "Add more files"
              : "Click to browse or drop files here"}
          </label>
          <input
            type="file"
            id="fileInput"
            onChange={handleFileChange}
            multiple
            style={{ display: "none" }}
          />
        </div>

        {/* Display uploaded file names */}
        <div className="uploaded-files">
          {fileNames.map((fileName, index) => (
            <div key={index} className="file-item">
              <span>{fileName}</span>
              <button
                type="button"
                className="remove-button"
                onClick={() => handleFileRemove(index)}
              >
                ‚ùå
              </button>
            </div>
          ))}
        </div>

        <input
          type="text"
          placeholder="Enter title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
        <select
          value={category}
          onChange={(e) => {
            setCategory(e.target.value);
            setSubCategory("");
          }}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          <option value="events">Events</option>
          <option value="clubs">Clubs</option>
          <option value="academics">Academics</option>
          <option value="notices">Notices</option>
          <option value="skillhub">Skillhub</option>
          <option value="placements">Placements</option>
        </select>
        {category && subCategoriesMap[category]?.length > 0 && (
          <select
            value={subCategory}
            onChange={(e) => setSubCategory(e.target.value)}
          >
            <option value="" disabled>
              Select a subcategory
            </option>
            {subCategoriesMap[category].map((sub, index) => (
              <option key={index} value={sub.toLowerCase()}>
                {sub}
              </option>
            ))}
          </select>
        )}
        <div className="button-container">
          <button type="button" onClick={() => handleSubmit(false)}>
            Submit Post
          </button>
          <button type="button" onClick={() => handleSubmit(true)}>
            Save as Draft
          </button>
        </div>
      </form>
    </div>
  );
};

export default NewPost;
